interface KPIProps {
  title: string;
  value: string | number;
  icon: React.ReactNode;
  trend?: string;
  color?: 'green' | 'red' | 'blue';
}

const DashboardKPI: React.FC<KPIProps> = ({ title, value, icon, trend, color = 'blue' }) => {
  const getColorClass = () => {
    switch (color) {
      case 'green': return 'text-green-600 bg-green-100';
      case 'red': return 'text-red-600 bg-red-100';
      default: return 'text-blue-600 bg-blue-100';
    }
  };

  const getTrendClass = () => trend?.includes('+') ? 'text-green-600' : 'text-red-600';

  return (
    <div className="kpi-card p-6 rounded-lg shadow-sm border border-gray-200 bg-white">
