export class ProvidersService {
  constructor(
    private providerRepo: Repository<Provider>,
    private proxyRepo: Repository<Proxy>,
    private httpService: HttpService,
    private configService: ConfigService,
  ) {}

  async findAll() {
    const providers = await this.providerRepo.find({
      relations: ['proxies'],
      order: { createdAt: 'DESC' },
    });
    return providers;
  }

  async findOne(id: string) {
    return await this.providerRepo.findOne({
      where: { id },
      relations: ['proxies'],
    });
  }

  async create(data: CreateProviderDto) {
    // Generate logoUrl if not provided
    if (!data.logoUrl) {
      if (data.config?.website) {
        data.logoUrl = `https://${data.config.website}/apple-touch-icon.png`;
      } else {
        data.logoUrl = null;
      }
    }

    const provider = this.providerRepo.create(data);
    return await this.providerRepo.save(provider);
  }

  async update(id: string, updateData: { active: boolean }) {
    const provider = await this.findOne(id);
    if (!provider) {
      throw new NotFoundException('Provider not found');
    }
    Object.assign(provider, updateData);
    return await this.providerRepo.save(provider);
  }

  async remove(id: string) {
    const provider = await this.findOne(id);
    if (!provider) {
      throw new NotFoundException('Provider not found');
    }
    await this.providerRepo.remove(provider);
    return provider;
  }

  async importProxies(providerId: string) {
    const provider = await this.findOne(providerId);
    if (!provider) {
      throw new NotFoundException('Provider not found');
    }

    // Example for API type (IPRoyal - extend for others)
    if (provider.type === 'api') {
      const { access_token: token, list_endpoint } = provider.config;
      if (!token || !list_endpoint) {
        throw new BadRequestException('Missing API config (access_token or list_endpoint)');
      }

      try {
        const response = await this.httpService.get(list_endpoint, {
          headers: {
            'X-Access-Token': token,
          },
        }).toPromise();

        const proxiesData = response.data; // Assume array of proxies

        // Clean up existing proxies for this provider (optional)
        await this.proxyRepo.delete({ provider: { id: providerId } });

        // Create and save new proxies
        const proxies = proxiesData.map((p: any) => this.proxyRepo.create({
          ...p,
          provider: provider,
          // Add default fields like status: 'pending', lastCheck: new Date(), etc.
          status: 'pending',
          lastCheck: new Date(),
        }));

        await this.proxyRepo.save(proxies);

        return { count: proxies.length };
      } catch (error) {
        throw new InternalServerErrorException(`Import failed: ${error.message}`);
      }
    } else if (provider.type === 'file') {
      // Handle file upload/import logic (e.g., parse CSV/JSON from uploaded file ID)
      throw new NotImplementedException('File import not implemented yet');
    } else if (provider.type === 'manual') {
      throw new NotImplementedException('Manual import not implemented yet');
    }

    return { count: 0 };
  }
}

