import { HandPlatter, LayoutDashboard, Settings, HandPlatterIcon, Users } from 'lucide-react'
import { Link } from 'react-router-dom'

const navItems = [
  { name: 'Dashboard', path: '/', icon: LayoutDashboard },
  { name: 'Proxies', path: '/proxies', icon: Users },
  { name: 'Providers', path: '/providers', icon: HandPlatter },
  { name: 'Settings', path: '/settings', icon: Settings },
]

export default function Sidebar() {
  return (
    <aside className="w-64 h-screen bg-side-panel p-4 flex flex-col space-y-4">
      <h1 className="text-xl font-bold">ProxyHub Admin</h1>
      <nav className="flex-1">
        {navItems.map((item) => {
          const Icon = item.icon
          return (
            <Link
              key={item.name}
              to={item.path}
              className="flex items-center space-x-2 p-2 rounded hover:bg-gray-800"
            >
              <Icon className="w-5 h-5" />
              <span className="">{item.name}</span>
            </Link>
          )
        })}
      </nav>
    </aside>
  )
}

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: 0 0% 100%;
  --foreground: 240 10% 3.9%;
  --card: 0 0% 100%;
  --card-foreground: 240 10% 3.9%;
  --popover: 0 0% 100%;
  --popover-foreground: 240 10% 3.9%;
  --primary: 240 5.3% 10%;
  --primary-foreground: 0 0% 98%;
  --secondary: 240 4.8% 95.9%;
  --secondary-foreground: 240 5.9% 10%;
  --muted: 240 4.8% 95.9%;
  --muted-foreground: 240 3.8% 46.1%;
  --accent: 240 4.8% 95.9%;
  --accent-foreground: 240 5.9% 10%;
  --destructive: 0 84.2% 60.2%;
  --destructive-foreground: 0 0% 98%;
  --border: 240 5.9% 90%;
  --input: 240 5.9% 90%;
  --ring: 240 5.9% 10%;
  --radius: 0.5rem;
  --side-panel: 240 0% 14%;
  --side-panel-text: 240 10% 4%;
}

.dark {
  --background: 0, 0%, 11%;
  --foreground: 0 0% 98%;
  --card: 0, 0%, 14%;
  --card-foreground: 0 0% 98%;
  --popover: 240 10% 3.9%;
  --popover-foreground: 0 0% 98%;
  --primary: 0 0% 98%;
  --primary-foreground: 240 5.3% 10%;
  --secondary: 240 3.7% 15.9%;
  --secondary-foreground: 0 0% 98%;
  --muted: 240 3.7% 15.9%;
  --muted-foreground: 240 5% 64.9%;
  --accent: 240 3.7% 15.9%;
  --accent-foreground: 0 0% 98%;
  --destructive: 0 62.8% 30.6%;
  --destructive-foreground: 0 0% 98%;
  --border: 240 3.7% 15.9%;
  --input: 240 3.7% 15.9%;
  --ring: 240 4.9% 83.9%;
  --side-panel: 0, 0%, 14%;
  --side-panel-text: 0 0% 100%;
}

@layer base {
  :root {
    color-scheme: light;
  }

  .dark {
    color-scheme: dark;
  }

  body {
    background: hsl(var(--background));
    color: hsl(var(--foreground));
  }

  .side-panel {
    background: hsl(var(--side-panel));
    color: hsl(var(--card-foreground));
  }
}

