import { Injectable } from "@nestjs/common";
import { PrismaClient } from "@prisma/client";

@Injectable()
export class ProxyService {
  constructor(private prisma: PrismaClient) {}

  async listProxies(query: any, sample?: boolean) {
    if (sample) {
      // Use raw query for random sample
      const rows = await this.prisma.$queryRawUnsafe(`SELECT id, host, pool, "providerId", country, city, region, latitude, longitude, asn, org, score FROM "Proxy" ORDER BY random() LIMIT 200`);
      return { items: rows };
    }

    const limit = Math.min(parseInt(query.limit || '200', 10), 5000);
    const where: any = {};
    if (query.pool) where.pool = query.pool;
    if (query.providerId) where.providerId = query.providerId;

    // Bbox filter
    if (query.bbox) {
      const parts = String(query.bbox).split(',').map(Number);
      if (parts.length === 4) {
        where.AND = [
          { latitude: { gte: parts[1] } },
          { latitude: { lte: parts[3] } },
          { longitude: { gte: parts[0] } },
          { longitude: { lte: parts[2] } },
        ];
      }
    }

    const rows = await this.prisma.proxy.findMany({
      where,
      select: { id: true, host: true, pool: true, providerId: true, country: true, city: true, region: true, latitude: true, longitude: true, asn: true, org: true, score: true },
      take: limit,
      orderBy: [{ score: 'desc' }, { lastChecked: 'desc' }]
    });
    return { items: rows };
  }

  async createProxy(data: any) {
    // Validate provider exists if providerId provided
    if (data.providerId) {
      const provider = await this.prisma.provider.findUnique({ where: { id: data.providerId } });
      if (!provider) {
        throw new Error('Provider not found');
      }
    }

    return this.prisma.proxy.create({
      data: {
        pool: data.pool,
        host: data.host,
        port: data.port,
        username: data.username,
        password: data.password,
        protocol: data.protocol || 'http',
        country: data.country,
        region: data.region,
        city: data.city,
        latitude: data.latitude,
        longitude: data.longitude,
        asn: data.asn,
        org: data.org,
        tags: data.tags || [],
        meta: data.meta,
        providerId: data.providerId,
      },
    });
  }

  async updateProxy(id: string, data: any) {
    return this.prisma.proxy.update({
      where: { id },
      data,
    });
  }

  async deleteProxy(id: string) {
    await this.prisma.proxy.delete({ where: { id } });
    return { success: true };
  }

  // Sample method - standalone if needed, but integrated into list
  async getSample() {
    const rows = await this.prisma.$queryRawUnsafe(`SELECT id, host, pool, "providerId", country, city, region, latitude, longitude, asn, org, score FROM "Proxy" ORDER BY random() LIMIT 200`);
    return { items: rows };
  }
}

