import { ApiProperty } from '@nestjs/swagger';
import { IsString, IsEnum, IsObject, IsOptional, IsBoolean, IsUrl, ValidateNested } from 'class-validator';
import { Type } from 'class-transformer';

export class ConfigDto {
  [key: string]: any;
}

export class CreateProviderDto {
  @ApiProperty({ description: 'Unique name for the provider' })
  @IsString()
  name: string;

  @ApiProperty({ enum: ['api', 'file', 'manual'], description: 'Type of provider (api, file, manual)' })
  @IsEnum(['api', 'file', 'manual'])
  type: string;

  @ApiProperty({ description: 'Configuration object for the provider' })
  @IsObject()
  @ValidateNested()
  @Type(() => ConfigDto)
  config: ConfigDto;

  @ApiProperty({ description: 'Optional logo URL', required: false })
  @IsOptional()
  @IsUrl()
  logoUrl?: string;

  @ApiProperty({ description: 'Whether the provider is active', required: false, default: true })
  @IsOptional()
  @IsBoolean()
  active?: boolean;
}

export class ProviderDto extends CreateProviderDto {
  @ApiProperty({ description: 'Unique ID' })
  @IsString()
  id: string;

  @ApiProperty({ description: 'Creation timestamp' })
  @IsString()
  createdAt: string;
}

export class PaginatedProvidersDto {
  @ApiProperty({ type: [ProviderDto] })
  items: ProviderDto[];

  @ApiProperty({ description: 'Total number of providers' })
  total: number;

  @ApiProperty({ description: 'Current page' })
  page: number;

  @ApiProperty({ description: 'Items per page' })
  limit: number;

  @ApiProperty({ description: 'Number of records to skip', required: false })
  skip?: number;
}

import * as React from "react"

import { cn } from "../../lib/utils"

const Switch = React.forwardRef<
  React.ElementRef<typeof React.Component>,
  React.ComponentPropsWithoutRef<typeof React.Component> & {
    checked?: boolean
    onCheckedChange?: (checked: boolean) => void
    disabled?: boolean
  }
>(({ className, checked, onCheckedChange, disabled, ...props }, ref) => {
  return (
    <div
      ref={ref}
      className={cn(
        "peer inline-flex h-[20px] w-[36px] shrink-0 cursor-pointer items-center rounded-full border-2 border-transparent transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 focus-visible:ring-offset-background disabled:cursor-not-allowed disabled:opacity-50 data-[state=checked]:bg-primary data-[state=unchecked]:bg-input",
        className
      )}
      {...props}
      role="switch"
      aria-checked={checked}
      data-state={checked ? "checked" : "unchecked"}
    >
      <div
        className={cn(
          "pointer-events-none block h-4 w-4 rounded-full bg-background shadow-lg ring-0 transition-transform data-[state=checked]:translate-x-[16px] data-[state=unchecked]:translate-x-0"
        )}
      />
    </div>
  )
})
Switch.displayName = "Switch"

export { Switch }

