  --border: 240 3.7% 15.9%;
  --input: 240 3.7% 15.9%;
  --ring: 240 4.9% 83.9%;
  --side-panel: 0, 0%, 14%;
  --side-panel-text: 0 0% 100%;
}

@layer base {
  :root {
    color-scheme: light;
