     1→import { useEffect, useState, useCallback } from 'react';
     2→import { useSearchParams } from 'react-router-dom';
     3→import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
     4→import { Users2, Plus, Edit, Trash2 } from 'lucide-react';
     5→import { cn } from '../lib/utils';
     6→import { Switch } from '../components/ui/switch';
     7→import { getProviders, createProvider, updateProvider, deleteProvider, getProvider, type Provider } from '../lib/api';
     8→
     9→const LIMIT = 10; // Fixed limit per Swagger (max 100)
    10→
    11→export default function Providers() {
    12→  const [searchParams] = useSearchParams();
    13→  const [providers, setProviders] = useState<Provider[]>([]);
    14→  const [loading, setLoading] = useState(true);
    15→  const [error, setError] = useState<string | null>(null);
    16→  const [search, setSearch] = useState(searchParams.get('search') || '');
    17→  const [page, setPage] = useState(1);
    18→  const [total, setTotal] = useState(0);
    19→  const [editingId, setEditingId] = useState<string | null>(null);
    20→  const [showModal, setShowModal] = useState(false);
    21→  const [editData, setEditData] = useState<Partial<Provider>>({});
    22→  const [mock, setMock] = useState(searchParams.get('mock') === 'true');
    23→  const [togglingId, setTogglingId] = useState<string | null>(null);
    24→
    25→  const fetchProviders = useCallback(async () => {
    26→    try {
    27→      setLoading(true);
    28→      const data = await getProviders({ page, limit: LIMIT, search: search || undefined });
    29→      console.log('Fetched data from API:', data); // Debug: Log full response
    30→      console.log('Setting providers to data.items:', data.items, 'Type:', typeof data.items, 'Is array:', Array.isArray(data.items)); // Debug: Log items specifically
    31→      if (!Array.isArray(data.items)) {
    32→        console.error('API response items is not an array:', data.items);
    33→        setProviders([]);
    34→        setTotal(0);
    35→        return;
    36→      }
    37→      setProviders(data.items);
    38→      setTotal(data.total);
    39→    } catch (err) {
    40→      setError(err instanceof Error ? err.message : 'Failed to fetch providers');
    41→    } finally {
    42→      setLoading(false);
    43→    }
    44→  }, [page, search, mock]);
    45→
    46→  useEffect(() => {
    47→    fetchProviders();
    48→  }, [fetchProviders]);
    49→
    50→  const handleCreate = async (data: Omit<Provider, 'id' | 'createdAt'>) => {
    51→    try {
    52→      await createProvider(data);
    53→      fetchProviders();
    54→      setShowModal(false);
    55→    } catch (err) {
    56→      setError(err instanceof Error ? err.message : 'Failed to create provider');
    57→    }
    58→  };
    59→
    60→  const handleUpdate = async (data: Partial<Provider>) => {
    61→    if (!editingId) return;
    62→    try {
    63→      await updateProvider(editingId, data);
    64→      fetchProviders();
    65→      setEditingId(null);
    66→      setShowModal(false);
    67→    } catch (err) {
    68→      setError(err instanceof Error ? err.message : 'Failed to update provider');
    69→    }
    70→  };
    71→
    72→  const handleToggle = useCallback(async (id: string) => {
    73→    if (togglingId === id) return;
    74→
    75→    const provider = providers.find(p => p.id === id);
    76→    if (!provider) return;
    77→
    78→    const currentActive = provider.active;
    79→    const newActive = !currentActive;
    80→
    81→    console.log(`Toggling provider ${id} to ${newActive ? 'active' : 'inactive'}`); // Debug log
    82→
    83→    setTogglingId(id);
    84→
    85→    try {
    86→      // Optimistic update
    87→      setProviders(prev => prev.map(p => p.id === id ? { ...p, active: newActive } : p));
    88→
    89→      await updateProvider(id, { active: newActive });
    90→
    91→      // Refetch to sync
    92→      await fetchProviders();
    93→    } catch (err) {
    94→      // Revert on error
    95→      setProviders(prev => prev.map(p => p.id === id ? { ...p, active: currentActive } : p));
    96→      setError(err instanceof Error ? err.message : 'Failed to toggle provider status');
    97→    } finally {
    98→      setTogglingId(null);
    99→    }
   100→  }, [providers, togglingId, updateProvider, fetchProviders]);
   101→
   102→  const handleDelete = async (id: string) => {
   103→    if (!confirm('Delete provider?')) return;
   104→    try {
   105→      await deleteProvider(id);
   106→      fetchProviders();
   107→    } catch (err) {
   108→      setError(err instanceof Error ? err.message : 'Failed to delete provider');
   109→    }
   110→  };
   111→
   112→  const openEdit = async (id: string) => {
   113→    try {
   114→      const provider = await getProvider(id);
   115→      setEditData(provider);
   116→      setEditingId(id);
   117→      setShowModal(true);
   118→    } catch (err) {
   119→      setError(err instanceof Error ? err.message : 'Failed to fetch provider');
   120→    }
   121→  };
   122→
   123→  if (loading) return <div className="p-8">Loading providers...</div>;
   124→  if (error) return <div className="p-8 text-red-500">{error}</div>;
   125→
   126→  return (
   127→    <div className="space-y-4">
   128→      <Card>
   129→        <CardHeader>
   130→          <div className="flex justify-between items-center">
   131→            <CardTitle className="flex items-center gap-2">
   132→              <Users2 className="w-5 h-5" />
   133→              Providers Management
   134→            </CardTitle>
   135→            <button onClick={() => setShowModal(true)} className="flex items-center gap-1 bg-primary text-primary-foreground px-4 py-2 rounded hover:bg-primary/90">
   136→              <Plus className="w-4 h-4" />
   137→              Add Provider
   138→            </button>
   139→          </div>
   140→        </CardHeader>
   141→        <CardContent>
   142→          <div className="flex gap-2 mb-4">
   143→            <input
   144→              type="text"
   145→              placeholder="Search providers..."
   146→              value={search}
   147→              onChange={(e) => setSearch(e.target.value)}
   148→              className="flex-1 px-3 py-2 border border-input rounded-md bg-background text-foreground placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:border-input"
   149→            />
   150→            <label className="flex items-center gap-2">
   151→              <input type="checkbox" checked={mock} onChange={(e) => setMock((e.target as HTMLInputElement).checked)} />
   152→              Mock Mode
   153→            </label>
   154→          </div>
   155→          <div className="space-y-2">
   156→            <div className="overflow-x-auto">
   157→              <table className="w-full">
   158→                <thead>
   159→                  <tr className="border-b border-border">
   160→                    <th className="text-left p-3 w-12">Logo</th>
   161→                    <th className="text-left p-3">Name</th>
   162→                    <th className="text-left p-3">Type</th>
   163→                    <th className="text-left p-3">Status</th>
   164→                    <th className="text-left p-3">Created</th>
   165→                    <th className="text-right p-3">Actions</th>
   166→                  </tr>
   167→                </thead>
   168→                <tbody>
   169→                  {!Array.isArray(providers) ? (
   170→                    <tr>
   171→                      <td colSpan={6} className="p-4 text-center text-red-500">
   172→                        Error: Providers data is not an array. Type: {typeof providers}, Value: {JSON.stringify(providers, null, 2)}
   173→                      </td>
   174→                    </tr>
   175→                  ) : providers.length === 0 ? (
   176→                    <tr>
   177→                      <td colSpan={6} className="p-4 text-center text-muted-foreground">
   178→                        No providers found
   179→                      </td>
   180→                    </tr>
   181→                  ) : (
   182→                    providers.map((provider) => (
   183→                      <tr key={provider.id} className="border-b border-border hover:bg-accent">
   184→                        <td className="p-3 w-12">
   185→                          {provider.logoUrl ? (
   186→                            <img src={provider.logoUrl} alt={provider.name} className="w-8 h-8 rounded object-cover" onError={(e) => { e.currentTarget.src = '/placeholder-logo.png'; }} />
   187→                          ) : (
   188→                            <div className="w-8 h-8 bg-gray-200 rounded flex items-center justify-center">
   189→                              <span className="text-gray-500 text-xs">{provider.name.charAt(0).toUpperCase()}</span>
   190→                            </div>
   191→                          )}
   192→                        </td>
   193→                        <td className="p-3">{provider.name}</td>
   194→                        <td className="p-3">
   195→                          <span className={cn('px-2 py-1 rounded text-xs', provider.active ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800')}>
   196→                            {provider.type}
   197→                          </span>
   198→                        </td>
   199→                        <td className="p-3">
   200→                          <span className={cn('px-2 py-1 rounded text-xs', provider.active ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800')}>
   201→                            {provider.active ? 'Active' : 'Inactive'}
   202→                          </span>
   203→                        </td>
