import * as React from 'react';
import { useEffect, useState, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Switch } from '../components/ui/switch';
import { Badge } from '../components/ui/badge';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '../components/ui/tooltip';
import { Button } from '../components/ui/button';
import { Globe, Zap, Plus, Edit, Trash2, Filter, Search } from 'lucide-react';
import { cn } from '../lib/utils';
import { getProxies, createProxy, updateProxy, deleteProxy, issueLease, getProviders, type Proxy, type CreateProxy, type UpdateProxy, type Provider } from '../lib/api';
import { DeleteConfirmModal } from '../components/DeleteConfirmModal';

const LIMIT = 10;

interface LeaseResponse {
  leaseId: string;
  proxy: string;
  protocol: string;
  expiresAt: string;
  meta: {
    providerId?: string;
    score: number;
    country?: string;
    sticky: boolean;
  };
}

interface TestResult {
  success: boolean;
  proxyString?: string;
  error?: string;
  score: number;
}

export default function Proxies() {
  const [proxies, setProxies] = useState<Proxy[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [search, setSearch] = useState('');
  const [selectedPool, setSelectedPool] = useState('');
  const [selectedProvider, setSelectedProvider] = useState('');
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const [showModal, setShowModal] = useState(false);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editData, setEditData] = useState<any>({});
  const [showTestModal, setShowTestModal] = useState(false);
  const [testingId, setTestingId] = useState<string | null>(null);
  const [testResult, setTestResult] = useState<TestResult | null>(null);
  const [togglingId, setTogglingId] = useState<string | null>(null);
  const [providers, setProviders] = useState<Provider[]>([]);
  const [fetchingProviders, setFetchingProviders] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [pendingDeleteId, setPendingDeleteId] = useState<string | null>(null);
  const [rememberChoice, setRememberChoice] = useState(false);

  // Fetch providers for filter dropdown
  useEffect(() => {
    const fetchProviders = async () => {
      setFetchingProviders(true);
      try {
        const data = await getProviders({ limit: 100 }); // Fetch all for dropdown
        setProviders(data.items);
      } catch (err) {
        console.error('Failed to fetch providers:', err);
      } finally {
        setFetchingProviders(false);
      }
    };
    fetchProviders();
  }, []);

  const fetchProxies = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const params: any = {
        page,
        limit: LIMIT,
      };
      if (search) params.search = search; // Assuming API supports search; if not, client-side
      if (selectedPool) params.pool = selectedPool;
      if (selectedProvider) params.providerId = selectedProvider;
      const data = await getProxies(params);
      setProxies(data.items);
      setTotal(data.total);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch proxies');
    } finally {
      setLoading(false);
    }
  }, [page, search, selectedPool, selectedProvider]);

  useEffect(() => {
    fetchProxies();
  }, [fetchProxies]);

  const resetFilters = () => {
    setSearch('');
    setSelectedPool('');
    setSelectedProvider('');
    setPage(1);
  };

  const handleCreate = async (data: CreateProxy) => {
    try {
      await createProxy(data);
      fetchProxies();
      setShowModal(false);
      setEditData({});
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to create proxy');
    }
  };

  const handleUpdate = async (data: UpdateProxy) => {
    if (!editingId) return;
    try {
      await updateProxy(editingId, data);
      fetchProxies();
      setEditingId(null);
      setShowModal(false);
      setEditData({});
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to update proxy');
    }
  };

  const handleToggle = useCallback(async (id: string, currentDisabled: boolean) => {
    if (togglingId === id) return;
    setTogglingId(id);
    try {
      // Optimistic update
      setProxies(prev => prev.map(p => p.id === id ? { ...p, disabled: !currentDisabled } : p));
      await updateProxy(id, { disabled: !currentDisabled });
      fetchProxies(); // Sync
    } catch (err) {
      // Revert
      setProxies(prev => prev.map(p => p.id === id ? { ...p, disabled: currentDisabled } : p));
      setError(err instanceof Error ? err.message : 'Failed to toggle proxy status');
    } finally {
      setTogglingId(null);
    }
  }, [togglingId]);

  const { confirmDelete } = useDeleteConfirmation();

  const handleDelete = async (id: string) => {
    const proceed = confirmDelete('proxy', async () => {
      try {
        await deleteProxy(id);
        fetchProxies();
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to delete proxy');
      }
    });
    if (!proceed) return;
  };

  const openEdit = async (id: string) => {
    try {
      // For simplicity, use current data from list; in full impl, fetch single
      const proxy = proxies.find(p => p.id === id);
      if (proxy) {
        setEditData({
          pool: proxy.pool,
          providerId: proxy.providerId,
          tags: proxy.tags,
          meta: proxy.meta,
          disabled: proxy.disabled,
        });
        setEditingId(id);
        setShowModal(true);
      }
    } catch (err) {
      setError('Failed to prepare edit');
    }
  };

  const handleTest = async (id: string) => {
    setTestingId(id);
    setTestResult(null);
    setShowTestModal(true);
    try {
      const proxy = proxies.find(p => p.id === id);
      if (!proxy || !proxy.pool) throw new Error('Invalid proxy');
      const leaseResponse = await issueLease({
        project: 'admin-test',
        pool: proxy.pool,
        sticky: false,
      });
      if ('error' in leaseResponse) {
        setTestResult({ success: false, error: leaseResponse.error, score: proxy.score });
      } else {
        // Simulate test: In real, fetch via proxy to external URL
        // For now, assume success if lease issued
        setTestResult({
          success: true,
          proxyString: leaseResponse.proxy,
          score: leaseResponse.meta.score,
        });
        // Optionally release lease immediately
        // await releaseLease(leaseResponse.leaseId, { status: 'ok' });
      }
    } catch (err) {
      setTestResult({
        success: false,
        error: err instanceof Error ? err.message : 'Test failed',
        score: proxies.find(p => p.id === id)?.score || 0,
      });
    } finally {
      setTestingId(null);
    }
  };

  if (loading) {
    return (
      <div className="p-8 flex items-center justify-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
        <span className="ml-2">Loading proxies...</span>
      </div>
    );
  }

  return (
    <div className="space-y-4 p-4">
      <Card>
        <CardHeader>
          <div className="flex justify-between items-center">
            <CardTitle className="flex items-center gap-2">
              <Globe className="w-5 h-5" />
              Proxies Management
              <TooltipProvider>
                <Tooltip>
                  <TooltipTrigger>
                    <span className="text-muted-foreground ml-1">(?)</span>
                  </TooltipTrigger>
                  <TooltipContent>
                    <p>Manage, test, and organize your uploaded proxies from various providers.</p>
                  </TooltipContent>
                </Tooltip>
              </TooltipProvider>
            </CardTitle>
            <Button onClick={() => setShowModal(true)} className="flex items-center gap-1 hover:scale-105 transition-transform">
              <Plus className="w-4 h-4" />
              Add Proxy
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          {/* Filters and Search */}
          <div className="flex flex-col md:flex-row gap-4 mb-4 p-4 bg-muted rounded-lg">
            <div className="flex-1 flex gap-2">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-muted-foreground w-4 h-4" />
                <input
                  type="text"
                  placeholder="Search proxies by host or tags..."
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-input rounded-md bg-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
                />
              </div>
            </div>
            <div className="flex gap-2">
              <select
                value={selectedPool}
                onChange={(e) => setSelectedPool(e.target.value)}
                className="px-3 py-2 border border-input rounded-md bg-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
              >
                <option value="">All Pools</option>
                <option value="residential">Residential</option>
                <option value="datacenter">Datacenter</option>
                <option value="test">Test</option>
                {/* Dynamically from data if needed */}
              </select>
              <select
                value={selectedProvider}
                onChange={(e) => setSelectedProvider(e.target.value)}
                disabled={fetchingProviders}
                className="px-3 py-2 border border-input rounded-md bg-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring disabled:opacity-50"
              >
                <option value="">All Providers</option>
                {providers.map(p => (
                  <option key={p.id} value={p.id}>{p.name}</option>
                ))}
              </select>
              <Button variant="outline" onClick={resetFilters} size="sm">
                Clear
              </Button>
            </div>
          </div>

          {/* Table */}
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border-b border-border">
                  <th className="text-left p-3">Host:Port</th>
                  <th className="text-left p-3">Pool</th>
                  <th className="text-left p-3">Provider</th>
                  <th className="text-left p-3">Location</th>
                  <th className="text-left p-3">Score</th>
                  <th className="text-left p-3">Status</th>
                  <th className="text-right p-3">Actions</th>
                </tr>
              </thead>
              <tbody>
                {proxies.length === 0 ? (
                  <tr>
                    <td colSpan={7} className="p-8 text-center text-muted-foreground">
                      No proxies found. {search || selectedPool || selectedProvider ? 'Try adjusting filters.' : 'Add one above!'}
                    </td>
                  </tr>
                ) : (
                  proxies.map((proxy) => {
                    const provider = providers.find(p => p.id === proxy.providerId);
                    return (
                      <tr key={proxy.id} className="border-b border-border hover:bg-accent data-[state=hover]:scale-[1.01] transition-transform">
                        <td className="p-3 font-mono">
                          <TooltipProvider>
                            <Tooltip>
                              <TooltipTrigger>
                                {proxy.host}:{proxy.port}
                              </TooltipTrigger>
                              <TooltipContent>
                                <p>Protocol: {proxy.protocol || 'http'}</p>
                                {proxy.username && <p>Auth: Username provided (masked)</p>}
                              </TooltipContent>
                            </Tooltip>
                          </TooltipProvider>
                        </td>
                        <td className="p-3">
                          <Badge variant="secondary">{proxy.pool || 'default'}</Badge>
                        </td>
                        <td className="p-3">
                          {provider ? provider.name : 'Unknown'}
                        </td>
                        <td className="p-3">
                          <TooltipProvider>
                            <Tooltip>
                              <TooltipTrigger>
                                <div className="flex items-center gap-1">
                                  <Globe className="w-4 h-4" />
                                  {proxy.country}, {proxy.city}
                                </div>
                              </TooltipTrigger>
                              <TooltipContent>
                                <p>Region: {proxy.region}</p>
                                {proxy.asn && <p>ASN: {proxy.asn} ({proxy.org})</p>}
                              </TooltipContent>
                            </Tooltip>
                          </TooltipProvider>
                        </td>
                        <td className="p-3">
                          <TooltipProvider>
                            <Tooltip>
                              <TooltipTrigger>
                                <Badge variant={proxy.score > 80 ? 'default' : 'secondary'}>
                                  {Math.round(proxy.score)}%
                                </Badge>
                              </TooltipTrigger>
                              <TooltipContent>
                                <p>Reliability score based on recent usage (higher is better).</p>
                                <p>Failures: {proxy.failedCount}</p>
                              </TooltipContent>
                            </Tooltip>
                          </TooltipProvider>
                        </td>
                        <td className="p-3">
                          <Switch
                            checked={!proxy.disabled}
                            onCheckedChange={() => handleToggle(proxy.id, proxy.disabled)}
                            disabled={togglingId === proxy.id}
                            className="data-[state=checked]:bg-green-500 data-[state=unchecked]:bg-red-500 transition-colors"
                          />
                        </td>
                        <td className="p-3 text-right space-x-1">
                          <TooltipProvider>
                            <Tooltip>
                              <TooltipTrigger asChild>
                                <Button variant="ghost" size="sm" onClick={() => handleTest(proxy.id)} className="hover:bg-green-100 dark:hover:bg-green-900 hover:scale-110 transition-all">
                                  <Zap className="w-4 h-4" />
                                </Button>
                              </TooltipTrigger>
                              <TooltipContent>Test proxy connectivity</TooltipContent>
                            </Tooltip>
                          </TooltipProvider>
                          <Button variant="ghost" size="sm" onClick={() => openEdit(proxy.id)} className="hover:bg-blue-100 dark:hover:bg-blue-900 hover:scale-110 transition-all">
                            <Edit className="w-4 h-4" />
                          </Button>
                          <Button variant="ghost" size="sm" onClick={() => handleDelete(proxy.id)} className="hover:bg-red-100 dark:hover:bg-red-900 hover:scale-110 transition-all">
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </td>
                      </tr>
                    );
                  })
                )}
              </tbody>
            </table>
          </div>

          {/* Pagination */}
          {total > LIMIT && (
            <div className="flex justify-between items-center mt-4">
              <Button
                variant="outline"
                onClick={() => setPage(p => Math.max(1, p - 1))}
                disabled={page === 1}
                className="hover:scale-105 transition-transform"
              >
                Previous
              </Button>
              <span className="text-sm text-muted-foreground">
                Page {page} of {Math.ceil(total / LIMIT)}
              </span>
              <Button
                variant="outline"
                onClick={() => setPage(p => p + 1)}
                disabled={proxies.length < LIMIT}
                className="hover:scale-105 transition-transform"
              >
                Next
              </Button>
            </div>
          )}

          {error && (
            <div className="p-4 bg-destructive/10 border border-destructive/30 rounded-md text-destructive">
              {error}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Create/Edit Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50" onClick={() => setShowModal(false)}>
          <div className="bg-background rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto" onClick={e => e.stopPropagation()}>
            <div className="p-6 border-b">
              <h2 className="text-xl font-semibold">
                {editingId ? 'Edit Proxy' : 'Add New Proxy'}
              </h2>
              <p className="text-sm text-muted-foreground mt-1">
                Configure proxy details. For authentication, enter credentials if required.
              </p>
            </div>
            <form
              onSubmit={async (e) => {
                e.preventDefault();
                const formData = new FormData(e.currentTarget);
                const data: any = {
                  host: formData.get('host') as string,
                  port: parseInt(formData.get('port') as string),
                  username: formData.get('username') as string || undefined,
                  password: formData.get('password') as string || undefined,
                  protocol: formData.get('protocol') as string || 'http',
                  pool: formData.get('pool') as string || undefined,
                  providerId: formData.get('providerId') as string || undefined,
                  tags: (formData.get('tags') as string)?.split(',').map(t => t.trim()).filter(Boolean) || undefined,
                  meta: formData.get('meta') ? JSON.parse(formData.get('meta') as string) : undefined,
                  disabled: formData.get('disabled') !== 'on', // Active by default unless disabled
                };
                editingId ? handleUpdate(data as UpdateProxy) : handleCreate(data as CreateProxy);
              }}
              className="p-6 space-y-4"
            >
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="host" className="block text-sm font-medium mb-2">
                    Host <span className="text-destructive">*</span>
                  </label>
                  <input
                    id="host"
                    name="host"
                    type="text"
                    defaultValue={editData.host}
                    required
                    placeholder="e.g., proxy.example.com"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
                <div>
                  <label htmlFor="port" className="block text-sm font-medium mb-2">
                    Port <span className="text-destructive">*</span>
                  </label>
                  <input
                    id="port"
                    name="port"
                    type="number"
                    defaultValue={editData.port}
                    required
                    min="1"
                    max="65535"
                    placeholder="8080"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
              </div>
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="protocol" className="block text-sm font-medium mb-2">
                    Protocol
                  </label>
                  <select
                    id="protocol"
                    name="protocol"
                    defaultValue={editData.protocol}
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  >
                    <option value="http">HTTP</option>
                    <option value="https">HTTPS</option>
                    <option value="socks4">SOCKS4</option>
                    <option value="socks5">SOCKS5</option>
                  </select>
                </div>
                <div>
                  <label htmlFor="pool" className="block text-sm font-medium mb-2">
                    Pool
                  </label>
                  <input
                    id="pool"
                    name="pool"
                    type="text"
                    defaultValue={editData.pool}
                    placeholder="e.g., residential"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
              </div>
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="providerId" className="block text-sm font-medium mb-2">
                    Provider
                  </label>
                  <select
                    id="providerId"
                    name="providerId"
                    defaultValue={editData.providerId}
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  >
                    <option value="">None</option>
                    {providers.map(p => (
                      <option key={p.id} value={p.id}>{p.name}</option>
                    ))}
                  </select>
                </div>
                <div>
                  <label htmlFor="username" className="block text-sm font-medium mb-2">
                    Username (Optional)
                  </label>
                  <input
                    id="username"
                    name="username"
                    type="text"
                    defaultValue={editData.username}
                    placeholder="username"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
              </div>
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="password" className="block text-sm font-medium mb-2">
                    Password (Optional)
                  </label>
                  <input
                    id="password"
                    name="password"
                    type="password"
                    defaultValue={editData.password ? '*****' : ''} // Masked display
                    placeholder="password"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
                <div>
                  <label className="flex items-center gap-2">
                    <input type="checkbox" name="disabled" defaultChecked={editData.disabled} className="rounded" />
                    <span className="text-sm">Disabled</span>
                  </label>
                </div>
              </div>
              <div>
                <label htmlFor="tags" className="block text-sm font-medium mb-2">
                  Tags (comma-separated)
                </label>
                <input
                  id="tags"
                  name="tags"
                  type="text"
                  defaultValue={editData.tags?.join(', ')}
                  placeholder="tag1, tag2, geo-us"
                  className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                />
              </div>
              <div>
                <label htmlFor="meta" className="block text-sm font-medium mb-2">
                  Meta (JSON, optional)
                </label>
                <textarea
                  id="meta"
                  name="meta"
                  rows={3}
                  defaultValue={JSON.stringify(editData.meta, null, 2)}
                  placeholder='{"custom": "value"}'
                  className="w-full px-3 py-2 border border-input rounded-md bg-background font-mono text-sm focus-visible:ring-2 focus-visible:ring-ring"
                />
                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger>
                      <span className="text-xs text-muted-foreground mt-1 block cursor-help">(Help)</span>
                    </TooltipTrigger>
                    <TooltipContent>Additional metadata as JSON. Use for custom fields not covered above.</TooltipContent>
                  </Tooltip>
                </TooltipProvider>
              </div>
              <div className="flex gap-3 pt-4 border-t">
                <Button type="button" variant="outline" onClick={() => { setShowModal(false); setEditData({}); setEditingId(null); }}>
                  Cancel
                </Button>
                <Button type="submit" className="ml-auto hover:scale-105 transition-transform">
                  {loading ? 'Saving...' : editingId ? 'Update' : 'Add Proxy'}
                </Button>
              </div>
              {error && <div className="text-destructive text-sm">{error}</div>}
            </form>
          </div>
        </div>
      )}

      {/* Test Modal */}
      {showTestModal && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50" onClick={() => setShowTestModal(false)}>
          <div className="bg-background rounded-lg shadow-xl max-w-md w-full" onClick={e => e.stopPropagation()}>
            <div className="p-6 border-b">
              <h2 className="text-xl font-semibold">Test Results for Proxy</h2>
            </div>
            <div className="p-6 space-y-4">
              {testResult ? (
                <>
                  <div className={cn('p-3 rounded-md', testResult.success ? 'bg-green-50 border border-green-200' : 'bg-red-50 border border-red-200')}>
                    <p className="font-medium">Status: {testResult.success ? 'Success' : 'Failed'}</p>
                    {testResult.success && testResult.proxyString && (
                      <div className="mt-2 p-2 bg-muted font-mono text-sm rounded">
                        {testResult.proxyString}
                      </div>
                    )}
                    {testResult.error && <p className="text-sm mt-1">Error: {testResult.error}</p>}
                    <p>Score: {testResult.score}%</p>
                  </div>
                  <Button onClick={() => setShowTestModal(false)} className="w-full">
                    Close
                  </Button>
                </>
              ) : (
                <div className="text-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-2"></div>
                  <p>Testing proxy... (issuing lease and checking connectivity)</p>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

import * as React from 'react';
import { useEffect, useState, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Switch } from '../components/ui/switch';
import { Badge } from '../components/ui/badge';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '../components/ui/tooltip';
import { Button } from '../components/ui/button';
import { Globe, Zap, Plus, Edit, Trash2, Filter, Search } from 'lucide-react';
import { cn } from '../lib/utils';
import { getProxies, createProxy, updateProxy, deleteProxy, issueLease, getProviders, type Proxy, type CreateProxy, type UpdateProxy, type Provider } from '../lib/api';
import { DeleteConfirmModal } from '../components/DeleteConfirmModal';

const LIMIT = 10;

interface LeaseResponse {
  leaseId: string;
  proxy: string;
  protocol: string;
  expiresAt: string;
  meta: {
    providerId?: string;
    score: number;
    country?: string;
    sticky: boolean;
  };
}

interface TestResult {
  success: boolean;
  proxyString?: string;
  error?: string;
  score: number;
}

export default function Proxies() {
  const [proxies, setProxies] = useState<Proxy[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [search, setSearch] = useState('');
  const [selectedPool, setSelectedPool] = useState('');
  const [selectedProvider, setSelectedProvider] = useState('');
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const [showModal, setShowModal] = useState(false);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editData, setEditData] = useState<any>({});
  const [showTestModal, setShowTestModal] = useState(false);
  const [testingId, setTestingId] = useState<string | null>(null);
  const [testResult, setTestResult] = useState<TestResult | null>(null);
  const [togglingId, setTogglingId] = useState<string | null>(null);
  const [providers, setProviders] = useState<Provider[]>([]);
  const [fetchingProviders, setFetchingProviders] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [pendingDeleteId, setPendingDeleteId] = useState<string | null>(null);
  const [rememberChoice, setRememberChoice] = useState(false);

  // Fetch providers for filter dropdown
  useEffect(() => {
    const fetchProviders = async () => {
      setFetchingProviders(true);
      try {
        const data = await getProviders({ limit: 100 }); // Fetch all for dropdown
        setProviders(data.items);
      } catch (err) {
        console.error('Failed to fetch providers:', err);
      } finally {
        setFetchingProviders(false);
      }
    };
    fetchProviders();
  }, []);

  const fetchProxies = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const params: any = {
        page,
        limit: LIMIT,
      };
      if (search) params.search = search; // Assuming API supports search; if not, client-side
      if (selectedPool) params.pool = selectedPool;
      if (selectedProvider) params.providerId = selectedProvider;
      const data = await getProxies(params);
      setProxies(data.items);
      setTotal(data.total);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch proxies');
    } finally {
      setLoading(false);
    }
  }, [page, search, selectedPool, selectedProvider]);

  useEffect(() => {
    fetchProxies();
  }, [fetchProxies]);

  const resetFilters = () => {
    setSearch('');
    setSelectedPool('');
    setSelectedProvider('');
    setPage(1);
  };

  const handleCreate = async (data: CreateProxy) => {
    try {
      await createProxy(data);
      fetchProxies();
      setShowModal(false);
      setEditData({});
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to create proxy');
    }
  };

  const handleUpdate = async (data: UpdateProxy) => {
    if (!editingId) return;
    try {
      await updateProxy(editingId, data);
      fetchProxies();
      setEditingId(null);
      setShowModal(false);
      setEditData({});
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to update proxy');
    }
  };

  const handleToggle = useCallback(async (id: string, currentDisabled: boolean) => {
    if (togglingId === id) return;
    setTogglingId(id);
    try {
      // Optimistic update
      setProxies(prev => prev.map(p => p.id === id ? { ...p, disabled: !currentDisabled } : p));
      await updateProxy(id, { disabled: !currentDisabled });
      fetchProxies(); // Sync
    } catch (err) {
      // Revert
      setProxies(prev => prev.map(p => p.id === id ? { ...p, disabled: currentDisabled } : p));
      setError(err instanceof Error ? err.message : 'Failed to toggle proxy status');
    } finally {
      setTogglingId(null);
    }
  }, [togglingId]);

  const { confirmDelete } = useDeleteConfirmation();

  const handleDelete = async (id: string) => {
    setPendingDeleteId(id);
    setShowDeleteModal(true);
  };

  const handleConfirmDelete = async () => {
    if (!pendingDeleteId) return;
    try {
      await deleteProxy(pendingDeleteId);
      fetchProxies();
      if (rememberChoice) {
        sessionStorage.setItem('deleteConfirmed', 'true');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to delete proxy');
    } finally {
      setShowDeleteModal(false);
      setPendingDeleteId(null);
      setRememberChoice(false);
    }
  };

  const handleRememberChange = (checked: boolean) => {
    setRememberChoice(checked);
  };

  const openEdit = async (id: string) => {
    try {
      // For simplicity, use current data from list; in full impl, fetch single
      const proxy = proxies.find(p => p.id === id);
      if (proxy) {
        setEditData({
          pool: proxy.pool,
          providerId: proxy.providerId,
          tags: proxy.tags,
          meta: proxy.meta,
          disabled: proxy.disabled,
        });
        setEditingId(id);
        setShowModal(true);
      }
    } catch (err) {
      setError('Failed to prepare edit');
    }
  };

  const handleTest = async (id: string) => {
    setTestingId(id);
    setTestResult(null);
    setShowTestModal(true);
    try {
      const proxy = proxies.find(p => p.id === id);
      if (!proxy || !proxy.pool) throw new Error('Invalid proxy');
      const leaseResponse = await issueLease({
        project: 'admin-test',
        pool: proxy.pool,
        sticky: false,
      });
      if ('error' in leaseResponse) {
        setTestResult({ success: false, error: leaseResponse.error, score: proxy.score });
      } else {
        // Simulate test: In real, fetch via proxy to external URL
        // For now, assume success if lease issued
        setTestResult({
          success: true,
          proxyString: leaseResponse.proxy,
          score: leaseResponse.meta.score,
        });
        // Optionally release lease immediately
        // await releaseLease(leaseResponse.leaseId, { status: 'ok' });
      }
    } catch (err) {
      setTestResult({
        success: false,
        error: err instanceof Error ? err.message : 'Test failed',
        score: proxies.find(p => p.id === id)?.score || 0,
      });
    } finally {
      setTestingId(null);
    }
  };

  if (loading) {
    return (
      <div className="p-8 flex items-center justify-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
        <span className="ml-2">Loading proxies...</span>
      </div>
    );
  }

  return (
    <div className="space-y-4 p-4">
      <Card>
        <CardHeader>
          <div className="flex justify-between items-center">
            <CardTitle className="flex items-center gap-2">
              <Globe className="w-5 h-5" />
              Proxies Management
              <TooltipProvider>
                <Tooltip>
                  <TooltipTrigger>
                    <span className="text-muted-foreground ml-1">(?)</span>
                  </TooltipTrigger>
                  <TooltipContent>
                    <p>Manage, test, and organize your uploaded proxies from various providers.</p>
                  </TooltipContent>
                </Tooltip>
              </TooltipProvider>
            </CardTitle>
            <Button onClick={() => setShowModal(true)} className="flex items-center gap-1 hover:scale-105 transition-transform">
              <Plus className="w-4 h-4" />
              Add Proxy
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          {/* Filters and Search */}
          <div className="flex flex-col md:flex-row gap-4 mb-4 p-4 bg-muted rounded-lg">
            <div className="flex-1 flex gap-2">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-muted-foreground w-4 h-4" />
                <input
                  type="text"
                  placeholder="Search proxies by host or tags..."
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-input rounded-md bg-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
                />
              </div>
            </div>
            <div className="flex gap-2">
              <select
                value={selectedPool}
                onChange={(e) => setSelectedPool(e.target.value)}
                className="px-3 py-2 border border-input rounded-md bg-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
              >
                <option value="">All Pools</option>
                <option value="residential">Residential</option>
                <option value="datacenter">Datacenter</option>
                <option value="test">Test</option>
                {/* Dynamically from data if needed */}
              </select>
              <select
                value={selectedProvider}
                onChange={(e) => setSelectedProvider(e.target.value)}
                disabled={fetchingProviders}
                className="px-3 py-2 border border-input rounded-md bg-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring disabled:opacity-50"
              >
                <option value="">All Providers</option>
                {providers.map(p => (
                  <option key={p.id} value={p.id}>{p.name}</option>
                ))}
              </select>
              <Button variant="outline" onClick={resetFilters} size="sm">
                Clear
              </Button>
            </div>
          </div>

          {/* Table */}
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border-b border-border">
                  <th className="text-left p-3">Host:Port</th>
                  <th className="text-left p-3">Pool</th>
                  <th className="text-left p-3">Provider</th>
                  <th className="text-left p-3">Location</th>
                  <th className="text-left p-3">Score</th>
                  <th className="text-left p-3">Status</th>
                  <th className="text-right p-3">Actions</th>
                </tr>
              </thead>
              <tbody>
                {proxies.length === 0 ? (
                  <tr>
                    <td colSpan={7} className="p-8 text-center text-muted-foreground">
                      No proxies found. {search || selectedPool || selectedProvider ? 'Try adjusting filters.' : 'Add one above!'}
                    </td>
                  </tr>
                ) : (
                  proxies.map((proxy) => {
                    const provider = providers.find(p => p.id === proxy.providerId);
                    return (
                      <tr key={proxy.id} className="border-b border-border hover:bg-accent data-[state=hover]:scale-[1.01] transition-transform">
                        <td className="p-3 font-mono">
                          <TooltipProvider>
                            <Tooltip>
                              <TooltipTrigger>
                                {proxy.host}:{proxy.port}
                              </TooltipTrigger>
                              <TooltipContent>
                                <p>Protocol: {proxy.protocol || 'http'}</p>
                                {proxy.username && <p>Auth: Username provided (masked)</p>}
                              </TooltipContent>
                            </Tooltip>
                          </TooltipProvider>
                        </td>
                        <td className="p-3">
                          <Badge variant="secondary">{proxy.pool || 'default'}</Badge>
                        </td>
                        <td className="p-3">
                          {provider ? provider.name : 'Unknown'}
                        </td>
                        <td className="p-3">
                          <TooltipProvider>
                            <Tooltip>
                              <TooltipTrigger>
                                <div className="flex items-center gap-1">
                                  <Globe className="w-4 h-4" />
                                  {proxy.country}, {proxy.city}
                                </div>
                              </TooltipTrigger>
                              <TooltipContent>
                                <p>Region: {proxy.region}</p>
                                {proxy.asn && <p>ASN: {proxy.asn} ({proxy.org})</p>}
                              </TooltipContent>
                            </Tooltip>
                          </TooltipProvider>
                        </td>
                        <td className="p-3">
                          <TooltipProvider>
                            <Tooltip>
                              <TooltipTrigger>
                                <Badge variant={proxy.score > 80 ? 'default' : 'secondary'}>
                                  {Math.round(proxy.score)}%
                                </Badge>
                              </TooltipTrigger>
                              <TooltipContent>
                                <p>Reliability score based on recent usage (higher is better).</p>
                                <p>Failures: {proxy.failedCount}</p>
                              </TooltipContent>
                            </Tooltip>
                          </TooltipProvider>
                        </td>
                        <td className="p-3">
                          <Switch
                            checked={!proxy.disabled}
                            onCheckedChange={() => handleToggle(proxy.id, proxy.disabled)}
                            disabled={togglingId === proxy.id}
                            className="data-[state=checked]:bg-green-500 data-[state=unchecked]:bg-red-500 transition-colors"
                          />
                        </td>
                        <td className="p-3 text-right space-x-1">
                          <TooltipProvider>
                            <Tooltip>
                              <TooltipTrigger asChild>
                                <Button variant="ghost" size="sm" onClick={() => handleTest(proxy.id)} className="hover:bg-green-100 dark:hover:bg-green-900 hover:scale-110 transition-all">
                                  <Zap className="w-4 h-4" />
                                </Button>
                              </TooltipTrigger>
                              <TooltipContent>Test proxy connectivity</TooltipContent>
                            </Tooltip>
                          </TooltipProvider>
                          <Button variant="ghost" size="sm" onClick={() => openEdit(proxy.id)} className="hover:bg-blue-100 dark:hover:bg-blue-900 hover:scale-110 transition-all">
                            <Edit className="w-4 h-4" />
                          </Button>
                          <Button variant="ghost" size="sm" onClick={() => handleDelete(proxy.id)} className="hover:bg-red-100 dark:hover:bg-red-900 hover:scale-110 transition-all">
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </td>
                      </tr>
                    );
                  })
                )}
              </tbody>
            </table>
          </div>

          {/* Pagination */}
          {total > LIMIT && (
            <div className="flex justify-between items-center mt-4">
              <Button
                variant="outline"
                onClick={() => setPage(p => Math.max(1, p - 1))}
                disabled={page === 1}
                className="hover:scale-105 transition-transform"
              >
                Previous
              </Button>
              <span className="text-sm text-muted-foreground">
                Page {page} of {Math.ceil(total / LIMIT)}
              </span>
              <Button
                variant="outline"
                onClick={() => setPage(p => p + 1)}
                disabled={proxies.length < LIMIT}
                className="hover:scale-105 transition-transform"
              >
                Next
              </Button>
            </div>
          )}

          {error && (
            <div className="p-4 bg-destructive/10 border border-destructive/30 rounded-md text-destructive">
              {error}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Create/Edit Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50" onClick={() => setShowModal(false)}>
          <div className="bg-background rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto" onClick={e => e.stopPropagation()}>
            <div className="p-6 border-b">
              <h2 className="text-xl font-semibold">
                {editingId ? 'Edit Proxy' : 'Add New Proxy'}
              </h2>
              <p className="text-sm text-muted-foreground mt-1">
                Configure proxy details. For authentication, enter credentials if required.
              </p>
            </div>
            <form
              onSubmit={async (e) => {
                e.preventDefault();
                const formData = new FormData(e.currentTarget);
                const data: any = {
                  host: formData.get('host') as string,
                  port: parseInt(formData.get('port') as string),
                  username: formData.get('username') as string || undefined,
                  password: formData.get('password') as string || undefined,
                  protocol: formData.get('protocol') as string || 'http',
                  pool: formData.get('pool') as string || undefined,
                  providerId: formData.get('providerId') as string || undefined,
                  tags: (formData.get('tags') as string)?.split(',').map(t => t.trim()).filter(Boolean) || undefined,
                  meta: formData.get('meta') ? JSON.parse(formData.get('meta') as string) : undefined,
                  disabled: formData.get('disabled') !== 'on', // Active by default unless disabled
                };
                editingId ? handleUpdate(data as UpdateProxy) : handleCreate(data as CreateProxy);
              }}
              className="p-6 space-y-4"
            >
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="host" className="block text-sm font-medium mb-2">
                    Host <span className="text-destructive">*</span>
                  </label>
                  <input
                    id="host"
                    name="host"
                    type="text"
                    defaultValue={editData.host}
                    required
                    placeholder="e.g., proxy.example.com"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
                <div>
                  <label htmlFor="port" className="block text-sm font-medium mb-2">
                    Port <span className="text-destructive">*</span>
                  </label>
                  <input
                    id="port"
                    name="port"
                    type="number"
                    defaultValue={editData.port}
                    required
                    min="1"
                    max="65535"
                    placeholder="8080"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
              </div>
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="protocol" className="block text-sm font-medium mb-2">
                    Protocol
                  </label>
                  <select
                    id="protocol"
                    name="protocol"
                    defaultValue={editData.protocol}
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  >
                    <option value="http">HTTP</option>
                    <option value="https">HTTPS</option>
                    <option value="socks4">SOCKS4</option>
                    <option value="socks5">SOCKS5</option>
                  </select>
                </div>
                <div>
                  <label htmlFor="pool" className="block text-sm font-medium mb-2">
                    Pool
                  </label>
                  <input
                    id="pool"
                    name="pool"
                    type="text"
                    defaultValue={editData.pool}
                    placeholder="e.g., residential"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
              </div>
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="providerId" className="block text-sm font-medium mb-2">
                    Provider
                  </label>
                  <select
                    id="providerId"
                    name="providerId"
                    defaultValue={editData.providerId}
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  >
                    <option value="">None</option>
                    {providers.map(p => (
                      <option key={p.id} value={p.id}>{p.name}</option>
                    ))}
                  </select>
                </div>
                <div>
                  <label htmlFor="username" className="block text-sm font-medium mb-2">
                    Username (Optional)
                  </label>
                  <input
                    id="username"
                    name="username"
                    type="text"
                    defaultValue={editData.username}
                    placeholder="username"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
              </div>
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="password" className="block text-sm font-medium mb-2">
                    Password (Optional)
                  </label>
                  <input
                    id="password"
                    name="password"
                    type="password"
                    defaultValue={editData.password ? '*****' : ''} // Masked display
                    placeholder="password"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
                <div>
                  <label className="flex items-center gap-2">
                    <input type="checkbox" name="disabled" defaultChecked={editData.disabled} className="rounded" />
                    <span className="text-sm">Disabled</span>
                  </label>
                </div>
              </div>
              <div>
                <label htmlFor="tags" className="block text-sm font-medium mb-2">
                  Tags (comma-separated)
                </label>
                <input
                  id="tags"
                  name="tags"
                  type="text"
                  defaultValue={editData.tags?.join(', ')}
                  placeholder="tag1, tag2, geo-us"
                  className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                />
              </div>
              <div>
                <label htmlFor="meta" className="block text-sm font-medium mb-2">
                  Meta (JSON, optional)
                </label>
                <textarea
                  id="meta"
                  name="meta"
                  rows={3}
                  defaultValue={JSON.stringify(editData.meta, null, 2)}
                  placeholder='{"custom": "value"}'
                  className="w-full px-3 py-2 border border-input rounded-md bg-background font-mono text-sm focus-visible:ring-2 focus-visible:ring-ring"
                />
                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger>
                      <span className="text-xs text-muted-foreground mt-1 block cursor-help">(Help)</span>
                    </TooltipTrigger>
                    <TooltipContent>Additional metadata as JSON. Use for custom fields not covered above.</TooltipContent>
                  </Tooltip>
                </TooltipProvider>
              </div>
              <div className="flex gap-3 pt-4 border-t">
                <Button type="button" variant="outline" onClick={() => { setShowModal(false); setEditData({}); setEditingId(null); }}>
                  Cancel
                </Button>
                <Button type="submit" className="ml-auto hover:scale-105 transition-transform">
                  {loading ? 'Saving...' : editingId ? 'Update' : 'Add Proxy'}
                </Button>
              </div>
              {error && <div className="text-destructive text-sm">{error}</div>}
            </form>
          </div>
        </div>
      )}

      {/* Test Modal */}
      {showTestModal && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50" onClick={() => setShowTestModal(false)}>
          <div className="bg-background rounded-lg shadow-xl max-w-md w-full" onClick={e => e.stopPropagation()}>
            <div className="p-6 border-b">
              <h2 className="text-xl font-semibold">Test Results for Proxy</h2>
            </div>
            <div className="p-6 space-y-4">
              {testResult ? (
                <>
                  <div className={cn('p-3 rounded-md', testResult.success ? 'bg-green-50 border border-green-200' : 'bg-red-50 border border-red-200')}>
                    <p className="font-medium">Status: {testResult.success ? 'Success' : 'Failed'}</p>
                    {testResult.success && testResult.proxyString && (
                      <div className="mt-2 p-2 bg-muted font-mono text-sm rounded">
                        {testResult.proxyString}
                      </div>
                    )}
                    {testResult.error && <p className="text-sm mt-1">Error: {testResult.error}</p>}
                    <p>Score: {testResult.score}%</p>
                  </div>
                  <Button onClick={() => setShowTestModal(false)} className="w-full">
                    Close
                  </Button>
                </>
              ) : (
                <div className="text-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-2"></div>
                  <p>Testing proxy... (issuing lease and checking connectivity)</p>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

import { useEffect, useState, useCallback } from 'react';
import { useSearchParams } from 'react-router-dom';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Users2, Plus, Edit, Trash2 } from 'lucide-react';
import { cn } from '../lib/utils';
import { Switch } from '../components/ui/switch';
import { getProviders, createProvider, updateProvider, deleteProvider, getProvider, type Provider } from '../lib/api';
import { DeleteConfirmModal } from '../components/DeleteConfirmModal';

const LIMIT = 10; // Fixed limit per Swagger (max 100)

export default function Providers() {
  const [searchParams] = useSearchParams();
  const [providers, setProviders] = useState<Provider[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [search, setSearch] = useState(searchParams.get('search') || '');
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [showModal, setShowModal] = useState(false);
  const [editData, setEditData] = useState<Partial<Provider>>({});
  const [mock, setMock] = useState(searchParams.get('mock') === 'true');
  const [togglingId, setTogglingId] = useState<string | null>(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [pendingDeleteId, setPendingDeleteId] = useState<string | null>(null);
  const [rememberChoice, setRememberChoice] = useState(false);

  const fetchProviders = useCallback(async () => {
    try {
      setLoading(true);
      const data = await getProviders({ page, limit: LIMIT, search: search || undefined });
      console.log('Fetched data from API:', data); // Debug: Log full response
      console.log('Setting providers to data.items:', data.items, 'Type:', typeof data.items, 'Is array:', Array.isArray(data.items)); // Debug: Log items specifically
      if (!Array.isArray(data.items)) {
        console.error('API response items is not an array:', data.items);
        setProviders([]);
        setTotal(0);
        return;
      }
      setProviders(data.items);
      setTotal(data.total);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch providers');
    } finally {
      setLoading(false);
    }
  }, [page, search, mock]);

  useEffect(() => {
    fetchProviders();
  }, [fetchProviders]);

  const handleCreate = async (data: Omit<Provider, 'id' | 'createdAt'>) => {
    try {
      await createProvider(data);
      fetchProviders();
      setShowModal(false);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to create provider');
    }
  };

  const handleUpdate = async (data: Partial<Provider>) => {
    if (!editingId) return;
    try {
      await updateProvider(editingId, data);
      fetchProviders();
      setEditingId(null);
      setShowModal(false);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to update provider');
    }
  };

  const handleToggle = useCallback(async (id: string) => {
    if (togglingId === id) return;

    const provider = providers.find(p => p.id === id);
    if (!provider) return;

    const currentActive = provider.active;
    const newActive = !currentActive;

    console.log(`Toggling provider ${id} to ${newActive ? 'active' : 'inactive'}`); // Debug log

    setTogglingId(id);

    try {
      // Optimistic update
      setProviders(prev => prev.map(p => p.id === id ? { ...p, active: newActive } : p));

      await updateProvider(id, { active: newActive });

      // Refetch to sync
      await fetchProviders();
    } catch (err) {
      // Revert on error
      setProviders(prev => prev.map(p => p.id === id ? { ...p, active: currentActive } : p));
      setError(err instanceof Error ? err.message : 'Failed to toggle provider status');
    } finally {
      setTogglingId(null);
    }
  }, [providers, togglingId, updateProvider, fetchProviders]);

  const { confirmDelete } = useDeleteConfirmation();

  const handleDelete = async (id: string) => {
    setPendingDeleteId(id);
    setShowDeleteModal(true);
  };

  const handleConfirmDelete = async () => {
    if (!pendingDeleteId) return;
    try {
      await deleteProvider(pendingDeleteId);
      fetchProviders();
      if (rememberChoice) {
        sessionStorage.setItem('deleteConfirmed', 'true');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to delete provider');
    } finally {
      setShowDeleteModal(false);
      setPendingDeleteId(null);
      setRememberChoice(false);
    }
  };

  const handleRememberChange = (checked: boolean) => {
    setRememberChoice(checked);
  };

  const openEdit = async (id: string) => {
    try {
      const provider = await getProvider(id);
      setEditData(provider);
      setEditingId(id);
      setShowModal(true);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch provider');
    }
  };

  if (loading) return <div className="p-8">Loading providers...</div>;
  if (error) return <div className="p-8 text-red-500">{error}</div>;

  return (
    <div className="space-y-4">
      <Card>
        <CardHeader>
          <div className="flex justify-between items-center">
            <CardTitle className="flex items-center gap-2">
              <Users2 className="w-5 h-5" />
              Providers Management
            </CardTitle>
            <button onClick={() => setShowModal(true)} className="flex items-center gap-1 bg-primary text-primary-foreground px-4 py-2 rounded hover:bg-primary/90">
              <Plus className="w-4 h-4" />
              Add Provider
            </button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="flex gap-2 mb-4">
            <input
              type="text"
              placeholder="Search providers..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="flex-1 px-3 py-2 border border-input rounded-md bg-background text-foreground placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:border-input"
            />
            <label className="flex items-center gap-2">
              <input type="checkbox" checked={mock} onChange={(e) => setMock((e.target as HTMLInputElement).checked)} />
              Mock Mode
            </label>
          </div>
          <div className="space-y-2">
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b border-border">
                    <th className="text-left p-3 w-12">Logo</th>
                    <th className="text-left p-3">Name</th>
                    <th className="text-left p-3">Type</th>
                    <th className="text-left p-3">Status</th>
                    <th className="text-left p-3">Created</th>
                    <th className="text-right p-3">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {!Array.isArray(providers) ? (
                    <tr>
                      <td colSpan={6} className="p-4 text-center text-red-500">
                        Error: Providers data is not an array. Type: {typeof providers}, Value: {JSON.stringify(providers, null, 2)}
                      </td>
                    </tr>
                  ) : providers.length === 0 ? (
                    <tr>
                      <td colSpan={6} className="p-4 text-center text-muted-foreground">
                        No providers found
                      </td>
                    </tr>
                  ) : (
                    providers.map((provider) => (
                      <tr key={provider.id} className="border-b border-border hover:bg-accent">
                        <td className="p-3 w-12">
                          {provider.logoUrl ? (
                            <img src={provider.logoUrl} alt={provider.name} className="w-8 h-8 rounded object-cover" onError={(e) => { e.currentTarget.src = '/placeholder-logo.png'; }} />
                          ) : (
                            <div className="w-8 h-8 bg-gray-200 rounded flex items-center justify-center">
                              <span className="text-gray-500 text-xs">{provider.name.charAt(0).toUpperCase()}</span>
                            </div>
                          )}
                        </td>
                        <td className="p-3">{provider.name}</td>
                        <td className="p-3">
                          <span className={cn('px-2 py-1 rounded text-xs', provider.active ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800')}>
                            {provider.type}
                          </span>
                        </td>
                        <td className="p-3">
                          <span className={cn('px-2 py-1 rounded text-xs', provider.active ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800')}>
                            {provider.active ? 'Active' : 'Inactive'}
                          </span>
                        </td>
                        <td className="p-3">{new Date(provider.createdAt).toLocaleDateString()}</td>
                        <td className="p-3 text-right">
                          <Switch
                            checked={provider.active}
                            onCheckedChange={(checked) => {
                              console.log(`Switch clicked for ${provider.id}, checked: ${checked}`); // Debug log
                              handleToggle(provider.id);
                            }}
                            disabled={togglingId === provider.id}
                            className={cn(
                              'data-[state=checked]:bg-green-600 data-[state=unchecked]:bg-red-600',
                              'w-10 h-5 mr-2'
                            )}
                          />
                          <button onClick={() => openEdit(provider.id)} className="p-1 text-blue-600 hover:text-blue-800" title="Edit provider details">
                            <Edit className="w-4 h-4" />
                          </button>
                          <button onClick={() => handleDelete(provider.id)} className="p-1 text-red-600 hover:text-red-800" title="Delete provider">
                            <Trash2 className="w-4 h-4" />
                          </button>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
            {providers.length === 0 && (
              <p className="text-center text-muted-foreground py-8">No providers found. Add one to get started!</p>
            )}
          </div>
          <div className="flex justify-between mt-4">
            <button onClick={() => setPage(page - 1)} disabled={page === 1} className="px-4 py-2 bg-primary text-primary-foreground rounded disabled:opacity-50">
              Previous
            </button>
            <span>{page} of {Math.ceil(total / LIMIT)}</span>
            <button onClick={() => setPage(page + 1)} disabled={providers.length < LIMIT} className="px-4 py-2 bg-primary text-primary-foreground rounded disabled:opacity-50">
              Next
            </button>
          </div>
        </CardContent>
      </Card>

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="w-full max-w-2xl bg-white rounded-lg shadow-xl">
            <div className="p-6 border-b">
              <h2 className="text-xl font-semibold">{editingId ? 'Edit Provider' : 'Add New Provider'}</h2>
              <p className="text-sm text-gray-600 mt-1">Set up a provider to import and manage your proxy sources. Choose a type that matches your setup. We'll guide you through the configuration.</p>
            </div>
            <form onSubmit={async (e) => {
              e.preventDefault();
              const formData = new FormData(e.currentTarget);
              const data = {
                name: formData.get('name') as string,
                type: (formData.get('type') as 'api' | 'file' | 'manual'),
                logoUrl: formData.get('logoUrl') as string,
                config: formData.get('config') ? JSON.parse(formData.get('config') as string) : {},
                active: formData.get('active') === 'on',
              };
              try {
                editingId ? await handleUpdate(data) : await handleCreate(data);
              } catch (err) {
                setError(err instanceof Error ? err.message : 'Failed to save provider');
              }
            }} className="p-6 space-y-6">
              <div className="grid md:grid-cols-2 gap-6">
                <div>
                  <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-2">
                    Provider Name <span className="text-red-500">*</span>
                  </label>
                  <input
                    id="name"
                    type="text"
                    name="name"
                    defaultValue={editData.name}
                    required
                    placeholder="e.g., Bright Data"
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors"
                  />
                </div>
                <div>
                  <label htmlFor="logoUrl" className="block text-sm font-medium text-gray-700 mb-2">
                    Logo URL (Optional)
                  </label>
                  <input
                    id="logoUrl"
                    type="url"
                    name="logoUrl"
                    defaultValue={editData.logoUrl || ''}
                    placeholder="https://example.com/logo.png"
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors"
                  />
                </div>
              </div>

              <div>
                <label htmlFor="type" className="block text-sm font-medium text-gray-700 mb-2">
                  Provider Type <span className="text-red-500">*</span>
                </label>
                <select
                  id="type"
                  name="type"
                  defaultValue={editData.type || 'api'}
                  required
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors"
                >
                  <option value="api">
                    API (Automated)
                  </option>
                  <option value="file">
                    File Upload
                  </option>
                  <option value="manual">
                    Manual Entry
                  </option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-4">
                  Configuration
                </label>
                <div className="space-y-2 mb-2">
                  <div className="flex items-center gap-2 text-xs text-gray-500 bg-blue-50 p-3 rounded-md border border-blue-100">
                    <svg className="w-4 h-4 text-blue-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.228 9c.549-1.165 2.03-2 3.772-2 2.21 0 4 1.343 4 3 0 1.4-1.278 2.575-3.006 2.907-.542.104-.994.54-.994 1.093m0 3h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                    <div>
                      <p>JSON Configuration</p>
                      <p className="mb-0">Configure API keys, endpoints, file paths, or other settings for your provider type. See <a href="https://docs.proxyhub.com/providers/config" target="_blank" className="text-blue-600 hover:underline">documentation</a> for type-specific examples.</p>
                    </div>
                  </div>
                </div>
                <textarea
                  name="config"
                  defaultValue={JSON.stringify(editData.config, null, 2)}
                  rows={8}
                  placeholder='{
  "apiKey": "your-api-key",
  "endpoint": "https://api.example.com/proxies",
  "authType": "bearer"
}'
                  className="w-full px-3 py-2 border border-gray-300 rounded-md font-mono text-sm bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-vertical transition-colors"
                  title="Enter JSON configuration. Use templates from documentation based on your provider type. Validate with a JSON linter if needed."
                />
                <div className="text-xs text-gray-500 mt-1">
                  Tip: Start with the example config from your provider's documentation. Common fields: apiKey, endpoint, username, password, filePath.
                </div>
              </div>

              <div className="flex items-center gap-3">
                <label className="flex items-center gap-2">
                  <input type="checkbox" name="active" defaultChecked={editData.active !== false} className="rounded" />
                  <span className="text-sm">Active</span>
                </label>
                <div className="ml-auto flex gap-3">
                  <button
                    type="button"
                    onClick={() => setShowModal(false)}
                    className="px-4 py-2 border border-gray-300 rounded-md hover:bg-gray-50 transition-colors"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                    disabled={loading}
                  >
                    {loading ? (
                      <span className="flex items-center gap-2">
                        <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" fill="none" viewBox="0 0 24 24">
                          <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
                          <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
                        </svg>
                        Saving...
                      </span>
                    ) : (
                      'Save Provider'
                    )}
                  </button>
                </div>
              </div>

              {error && (
                <div className="p-4 bg-red-50 border border-red-200 rounded-md">
                  <p className="text-sm text-red-800">{error}</p>
                </div>
              )}
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

import * as React from 'react';
import { useEffect, useState, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Switch } from '../components/ui/switch';
import { Badge } from '../components/ui/badge';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '../components/ui/tooltip';
import { Button } from '../components/ui/button';
import { Globe, Zap, Plus, Edit, Trash2, Filter, Search } from 'lucide-react';
import { cn } from '../lib/utils';
import { getProxies, createProxy, updateProxy, deleteProxy, issueLease, getProviders, type Proxy, type CreateProxy, type UpdateProxy, type Provider } from '../lib/api';
import { DeleteConfirmModal } from '../components/DeleteConfirmModal';

const LIMIT = 10;

interface LeaseResponse {
  leaseId: string;
  proxy: string;
  protocol: string;
  expiresAt: string;
  meta: {
    providerId?: string;
    score: number;
    country?: string;
    sticky: boolean;
  };
}

interface TestResult {
  success: boolean;
  proxyString?: string;
  error?: string;
  score: number;
}

export default function Proxies() {
  const [proxies, setProxies] = useState<Proxy[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [search, setSearch] = useState('');
  const [selectedPool, setSelectedPool] = useState('');
  const [selectedProvider, setSelectedProvider] = useState('');
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const [showModal, setShowModal] = useState(false);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editData, setEditData] = useState<any>({});
  const [showTestModal, setShowTestModal] = useState(false);
  const [testingId, setTestingId] = useState<string | null>(null);
  const [testResult, setTestResult] = useState<TestResult | null>(null);
  const [togglingId, setTogglingId] = useState<string | null>(null);
  const [providers, setProviders] = useState<Provider[]>([]);
  const [fetchingProviders, setFetchingProviders] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [pendingDeleteId, setPendingDeleteId] = useState<string | null>(null);
  const [rememberChoice, setRememberChoice] = useState(false);

  // Fetch providers for filter dropdown
  useEffect(() => {
    const fetchProviders = async () => {
      setFetchingProviders(true);
      try {
        const data = await getProviders({ limit: 100 }); // Fetch all for dropdown
        setProviders(data.items);
      } catch (err) {
        console.error('Failed to fetch providers:', err);
      } finally {
        setFetchingProviders(false);
      }
    };
    fetchProviders();
  }, []);

  const fetchProxies = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const params: any = {
        page,
        limit: LIMIT,
      };
      if (search) params.search = search; // Assuming API supports search; if not, client-side
      if (selectedPool) params.pool = selectedPool;
      if (selectedProvider) params.providerId = selectedProvider;
      const data = await getProxies(params);
      setProxies(data.items);
      setTotal(data.total);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch proxies');
    } finally {
      setLoading(false);
    }
  }, [page, search, selectedPool, selectedProvider]);

  useEffect(() => {
    fetchProxies();
  }, [fetchProxies]);

  const resetFilters = () => {
    setSearch('');
    setSelectedPool('');
    setSelectedProvider('');
    setPage(1);
  };

  const handleCreate = async (data: CreateProxy) => {
    try {
      await createProxy(data);
      fetchProxies();
      setShowModal(false);
      setEditData({});
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to create proxy');
    }
  };

  const handleUpdate = async (data: UpdateProxy) => {
    if (!editingId) return;
    try {
      await updateProxy(editingId, data);
      fetchProxies();
      setEditingId(null);
      setShowModal(false);
      setEditData({});
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to update proxy');
    }
  };

  const handleToggle = useCallback(async (id: string, currentDisabled: boolean) => {
    if (togglingId === id) return;
    setTogglingId(id);
    try {
      // Optimistic update
      setProxies(prev => prev.map(p => p.id === id ? { ...p, disabled: !currentDisabled } : p));
      await updateProxy(id, { disabled: !currentDisabled });
      fetchProxies(); // Sync
    } catch (err) {
      // Revert
      setProxies(prev => prev.map(p => p.id === id ? { ...p, disabled: currentDisabled } : p));
      setError(err instanceof Error ? err.message : 'Failed to toggle proxy status');
    } finally {
      setTogglingId(null);
    }
  }, [togglingId]);

  const { confirmDelete } = useDeleteConfirmation();

  const handleDelete = async (id: string) => {
    setPendingDeleteId(id);
    setShowDeleteModal(true);
  };

  const handleConfirmDelete = async () => {
    if (!pendingDeleteId) return;
    try {
      await deleteProxy(pendingDeleteId);
      fetchProxies();
      if (rememberChoice) {
        sessionStorage.setItem('deleteConfirmed', 'true');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to delete proxy');
    } finally {
      setShowDeleteModal(false);
      setPendingDeleteId(null);
      setRememberChoice(false);
    }
  };

  const handleRememberChange = (checked: boolean) => {
    setRememberChoice(checked);
  };

  const openEdit = async (id: string) => {
    try {
      // For simplicity, use current data from list; in full impl, fetch single
      const proxy = proxies.find(p => p.id === id);
      if (proxy) {
        setEditData({
          pool: proxy.pool,
          providerId: proxy.providerId,
          tags: proxy.tags,
          meta: proxy.meta,
          disabled: proxy.disabled,
        });
        setEditingId(id);
        setShowModal(true);
      }
    } catch (err) {
      setError('Failed to prepare edit');
    }
  };

  const handleTest = async (id: string) => {
    setTestingId(id);
    setTestResult(null);
    setShowTestModal(true);
    try {
      const proxy = proxies.find(p => p.id === id);
      if (!proxy || !proxy.pool) throw new Error('Invalid proxy');
      const leaseResponse = await issueLease({
        project: 'admin-test',
        pool: proxy.pool,
        sticky: false,
      });
      if ('error' in leaseResponse) {
        setTestResult({ success: false, error: leaseResponse.error, score: proxy.score });
      } else {
        // Simulate test: In real, fetch via proxy to external URL
        // For now, assume success if lease issued
        setTestResult({
          success: true,
          proxyString: leaseResponse.proxy,
          score: leaseResponse.meta.score,
        });
        // Optionally release lease immediately
        // await releaseLease(leaseResponse.leaseId, { status: 'ok' });
      }
    } catch (err) {
      setTestResult({
        success: false,
        error: err instanceof Error ? err.message : 'Test failed',
        score: proxies.find(p => p.id === id)?.score || 0,
      });
    } finally {
      setTestingId(null);
    }
  };

  if (loading) {
    return (
      <div className="p-8 flex items-center justify-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
        <span className="ml-2">Loading proxies...</span>
      </div>
    );
  }

  return (
    <div className="space-y-4 p-4">
      <Card>
        <CardHeader>
          <div className="flex justify-between items-center">
            <CardTitle className="flex items-center gap-2">
              <Globe className="w-5 h-5" />
              Proxies Management
              <TooltipProvider>
                <Tooltip>
                  <TooltipTrigger>
                    <span className="text-muted-foreground ml-1">(?)</span>
                  </TooltipTrigger>
                  <TooltipContent>
                    <p>Manage, test, and organize your uploaded proxies from various providers.</p>
                  </TooltipContent>
                </Tooltip>
              </TooltipProvider>
            </CardTitle>
            <Button onClick={() => setShowModal(true)} className="flex items-center gap-1 hover:scale-105 transition-transform">
              <Plus className="w-4 h-4" />
              Add Proxy
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          {/* Filters and Search */}
          <div className="flex flex-col md:flex-row gap-4 mb-4 p-4 bg-muted rounded-lg">
            <div className="flex-1 flex gap-2">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-muted-foreground w-4 h-4" />
                <input
                  type="text"
                  placeholder="Search proxies by host or tags..."
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-input rounded-md bg-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
                />
              </div>
            </div>
            <div className="flex gap-2">
              <select
                value={selectedPool}
                onChange={(e) => setSelectedPool(e.target.value)}
                className="px-3 py-2 border border-input rounded-md bg-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
              >
                <option value="">All Pools</option>
                <option value="residential">Residential</option>
                <option value="datacenter">Datacenter</option>
                <option value="test">Test</option>
                {/* Dynamically from data if needed */}
              </select>
              <select
                value={selectedProvider}
                onChange={(e) => setSelectedProvider(e.target.value)}
                disabled={fetchingProviders}
                className="px-3 py-2 border border-input rounded-md bg-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring disabled:opacity-50"
              >
                <option value="">All Providers</option>
                {providers.map(p => (
                  <option key={p.id} value={p.id}>{p.name}</option>
                ))}
              </select>
              <Button variant="outline" onClick={resetFilters} size="sm">
                Clear
              </Button>
            </div>
          </div>

          {/* Table */}
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border-b border-border">
                  <th className="text-left p-3">Host:Port</th>
                  <th className="text-left p-3">Pool</th>
                  <th className="text-left p-3">Provider</th>
                  <th className="text-left p-3">Location</th>
                  <th className="text-left p-3">Score</th>
                  <th className="text-left p-3">Status</th>
                  <th className="text-right p-3">Actions</th>
                </tr>
              </thead>
              <tbody>
                {proxies.length === 0 ? (
                  <tr>
                    <td colSpan={7} className="p-8 text-center text-muted-foreground">
                      No proxies found. {search || selectedPool || selectedProvider ? 'Try adjusting filters.' : 'Add one above!'}
                    </td>
                  </tr>
                ) : (
                  proxies.map((proxy) => {
                    const provider = providers.find(p => p.id === proxy.providerId);
                    return (
                      <tr key={proxy.id} className="border-b border-border hover:bg-accent data-[state=hover]:scale-[1.01] transition-transform">
                        <td className="p-3 font-mono">
                          <TooltipProvider>
                            <Tooltip>
                              <TooltipTrigger>
                                {proxy.host}:{proxy.port}
                              </TooltipTrigger>
                              <TooltipContent>
                                <p>Protocol: {proxy.protocol || 'http'}</p>
                                {proxy.username && <p>Auth: Username provided (masked)</p>}
                              </TooltipContent>
                            </Tooltip>
                          </TooltipProvider>
                        </td>
                        <td className="p-3">
                          <Badge variant="secondary">{proxy.pool || 'default'}</Badge>
                        </td>
                        <td className="p-3">
                          {provider ? provider.name : 'Unknown'}
                        </td>
                        <td className="p-3">
                          <TooltipProvider>
                            <Tooltip>
                              <TooltipTrigger>
                                <div className="flex items-center gap-1">
                                  <Globe className="w-4 h-4" />
                                  {proxy.country}, {proxy.city}
                                </div>
                              </TooltipTrigger>
                              <TooltipContent>
                                <p>Region: {proxy.region}</p>
                                {proxy.asn && <p>ASN: {proxy.asn} ({proxy.org})</p>}
                              </TooltipContent>
                            </Tooltip>
                          </TooltipProvider>
                        </td>
                        <td className="p-3">
                          <TooltipProvider>
                            <Tooltip>
                              <TooltipTrigger>
                                <Badge variant={proxy.score > 80 ? 'default' : 'secondary'}>
                                  {Math.round(proxy.score)}%
                                </Badge>
                              </TooltipTrigger>
                              <TooltipContent>
                                <p>Reliability score based on recent usage (higher is better).</p>
                                <p>Failures: {proxy.failedCount}</p>
                              </TooltipContent>
                            </Tooltip>
                          </TooltipProvider>
                        </td>
                        <td className="p-3">
                          <Switch
                            checked={!proxy.disabled}
                            onCheckedChange={() => handleToggle(proxy.id, proxy.disabled)}
                            disabled={togglingId === proxy.id}
                            className="data-[state=checked]:bg-green-500 data-[state=unchecked]:bg-red-500 transition-colors"
                          />
                        </td>
                        <td className="p-3 text-right space-x-1">
                          <TooltipProvider>
                            <Tooltip>
                              <TooltipTrigger asChild>
                                <Button variant="ghost" size="sm" onClick={() => handleTest(proxy.id)} className="hover:bg-green-100 dark:hover:bg-green-900 hover:scale-110 transition-all">
                                  <Zap className="w-4 h-4" />
                                </Button>
                              </TooltipTrigger>
                              <TooltipContent>Test proxy connectivity</TooltipContent>
                            </Tooltip>
                          </TooltipProvider>
                          <Button variant="ghost" size="sm" onClick={() => openEdit(proxy.id)} className="hover:bg-blue-100 dark:hover:bg-blue-900 hover:scale-110 transition-all">
                            <Edit className="w-4 h-4" />
                          </Button>
                          <Button variant="ghost" size="sm" onClick={() => handleDelete(proxy.id)} className="hover:bg-red-100 dark:hover:bg-red-900 hover:scale-110 transition-all">
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </td>
                      </tr>
                    );
                  })
                )}
              </tbody>
            </table>
          </div>

          {/* Pagination */}
          {total > LIMIT && (
            <div className="flex justify-between items-center mt-4">
              <Button
                variant="outline"
                onClick={() => setPage(p => Math.max(1, p - 1))}
                disabled={page === 1}
                className="hover:scale-105 transition-transform"
              >
                Previous
              </Button>
              <span className="text-sm text-muted-foreground">
                Page {page} of {Math.ceil(total / LIMIT)}
              </span>
              <Button
                variant="outline"
                onClick={() => setPage(p => p + 1)}
                disabled={proxies.length < LIMIT}
                className="hover:scale-105 transition-transform"
              >
                Next
              </Button>
            </div>
          )}

          {error && (
            <div className="p-4 bg-destructive/10 border border-destructive/30 rounded-md text-destructive">
              {error}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Create/Edit Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50" onClick={() => setShowModal(false)}>
          <div className="bg-background rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto" onClick={e => e.stopPropagation()}>
            <div className="p-6 border-b">
              <h2 className="text-xl font-semibold">
                {editingId ? 'Edit Proxy' : 'Add New Proxy'}
              </h2>
              <p className="text-sm text-muted-foreground mt-1">
                Configure proxy details. For authentication, enter credentials if required.
              </p>
            </div>
            <form
              onSubmit={async (e) => {
                e.preventDefault();
                const formData = new FormData(e.currentTarget);
                const data: any = {
                  host: formData.get('host') as string,
                  port: parseInt(formData.get('port') as string),
                  username: formData.get('username') as string || undefined,
                  password: formData.get('password') as string || undefined,
                  protocol: formData.get('protocol') as string || 'http',
                  pool: formData.get('pool') as string || undefined,
                  providerId: formData.get('providerId') as string || undefined,
                  tags: (formData.get('tags') as string)?.split(',').map(t => t.trim()).filter(Boolean) || undefined,
                  meta: formData.get('meta') ? JSON.parse(formData.get('meta') as string) : undefined,
                  disabled: formData.get('disabled') !== 'on', // Active by default unless disabled
                };
                editingId ? handleUpdate(data as UpdateProxy) : handleCreate(data as CreateProxy);
              }}
              className="p-6 space-y-4"
            >
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="host" className="block text-sm font-medium mb-2">
                    Host <span className="text-destructive">*</span>
                  </label>
                  <input
                    id="host"
                    name="host"
                    type="text"
                    defaultValue={editData.host}
                    required
                    placeholder="e.g., proxy.example.com"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
                <div>
                  <label htmlFor="port" className="block text-sm font-medium mb-2">
                    Port <span className="text-destructive">*</span>
                  </label>
                  <input
                    id="port"
                    name="port"
                    type="number"
                    defaultValue={editData.port}
                    required
                    min="1"
                    max="65535"
                    placeholder="8080"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
              </div>
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="protocol" className="block text-sm font-medium mb-2">
                    Protocol
                  </label>
                  <select
                    id="protocol"
                    name="protocol"
                    defaultValue={editData.protocol}
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  >
                    <option value="http">HTTP</option>
                    <option value="https">HTTPS</option>
                    <option value="socks4">SOCKS4</option>
                    <option value="socks5">SOCKS5</option>
                  </select>
                </div>
                <div>
                  <label htmlFor="pool" className="block text-sm font-medium mb-2">
                    Pool
                  </label>
                  <input
                    id="pool"
                    name="pool"
                    type="text"
                    defaultValue={editData.pool}
                    placeholder="e.g., residential"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
              </div>
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="providerId" className="block text-sm font-medium mb-2">
                    Provider
                  </label>
                  <select
                    id="providerId"
                    name="providerId"
                    defaultValue={editData.providerId}
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  >
                    <option value="">None</option>
                    {providers.map(p => (
                      <option key={p.id} value={p.id}>{p.name}</option>
                    ))}
                  </select>
                </div>
                <div>
                  <label htmlFor="username" className="block text-sm font-medium mb-2">
                    Username (Optional)
                  </label>
                  <input
                    id="username"
                    name="username"
                    type="text"
                    defaultValue={editData.username}
                    placeholder="username"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
              </div>
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="password" className="block text-sm font-medium mb-2">
                    Password (Optional)
                  </label>
                  <input
                    id="password"
                    name="password"
                    type="password"
                    defaultValue={editData.password ? '*****' : ''} // Masked display
                    placeholder="password"
                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                  />
                </div>
                <div>
                  <label className="flex items-center gap-2">
                    <input type="checkbox" name="disabled" defaultChecked={editData.disabled} className="rounded" />
                    <span className="text-sm">Disabled</span>
                  </label>
                </div>
              </div>
              <div>
                <label htmlFor="tags" className="block text-sm font-medium mb-2">
                  Tags (comma-separated)
                </label>
                <input
                  id="tags"
                  name="tags"
                  type="text"
                  defaultValue={editData.tags?.join(', ')}
                  placeholder="tag1, tag2, geo-us"
                  className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
                />
              </div>
              <div>
                <label htmlFor="meta" className="block text-sm font-medium mb-2">
                  Meta (JSON, optional)
                </label>
                <textarea
                  id="meta"
                  name="meta"
                  rows={3}
                  defaultValue={JSON.stringify(editData.meta, null, 2)}
                  placeholder='{"custom": "value"}'
                  className="w-full px-3 py-2 border border-input rounded-md bg-background font-mono text-sm focus-visible:ring-2 focus-visible:ring-ring"
                />
                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger>
                      <span className="text-xs text-muted-foreground mt-1 block cursor-help">(Help)</span>
                    </TooltipTrigger>
                    <TooltipContent>Additional metadata as JSON. Use for custom fields not covered above.</TooltipContent>
                  </Tooltip>
                </TooltipProvider>
              </div>
              <div className="flex gap-3 pt-4 border-t">
                <Button type="button" variant="outline" onClick={() => { setShowModal(false); setEditData({}); setEditingId(null); }}>
                  Cancel
                </Button>
                <Button type="submit" className="ml-auto hover:scale-105 transition-transform">
                  {loading ? 'Saving...' : editingId ? 'Update' : 'Add Proxy'}
                </Button>
              </div>
              {error && <div className="text-destructive text-sm">{error}</div>}
            </form>
          </div>
        </div>
      )}

      {/* Test Modal */}
      {showTestModal && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50" onClick={() => setShowTestModal(false)}>
          <div className="bg-background rounded-lg shadow-xl max-w-md w-full" onClick={e => e.stopPropagation()}>
            <div className="p-6 border-b">
              <h2 className="text-xl font-semibold">Test Results for Proxy</h2>
            </div>
            <div className="p-6 space-y-4">
              {testResult ? (
                <>
                  <div className={cn('p-3 rounded-md', testResult.success ? 'bg-green-50 border border-green-200' : 'bg-red-50 border border-red-200')}>
                    <p className="font-medium">Status: {testResult.success ? 'Success' : 'Failed'}</p>
                    {testResult.success && testResult.proxyString && (
                      <div className="mt-2 p-2 bg-muted font-mono text-sm rounded">
                        {testResult.proxyString}
                      </div>
                    )}
                    {testResult.error && <p className="text-sm mt-1">Error: {testResult.error}</p>}
                    <p>Score: {testResult.score}%</p>
                  </div>
                  <Button onClick={() => setShowTestModal(false)} className="w-full">
                    Close
                  </Button>
                </>
              ) : (
                <div className="text-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-2"></div>
                  <p>Testing proxy... (issuing lease and checking connectivity)</p>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
        </div>
      </Card>

      <DeleteConfirmModal
        isOpen={showDeleteModal}
        onClose={() => { setShowDeleteModal(false); setPendingDeleteId(null); setRememberChoice(false); }}
        onConfirm={handleConfirmDelete}
        itemType="proxy"
        rememberChoice={rememberChoice}
        onRememberChange={handleRememberChange}
      />
    </div>
  );
}
