     1→import { useEffect, useState, useCallback } from 'react';
     2→import { useSearchParams } from 'react-router-dom';
     3→import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
     4→import { Users2, Plus, Edit, Trash2 } from 'lucide-react';
     5→import { cn } from '../lib/utils';
     6→import { Switch } from '../components/ui/switch';
     7→import { getProviders, createProvider, updateProvider, deleteProvider, getProvider, type Provider } from '../lib/api';
     8→import { DeleteConfirmModal } from '../components/DeleteConfirmModal';
     9→
    10→const LIMIT = 10; // Fixed limit per Swagger (max 100)
    11→
    12→export default function Providers() {
    13→  const [searchParams] = useSearchParams();
    14→  const [providers, setProviders] = useState<Provider[]>([]);
    15→  const [loading, setLoading] = useState(true);
    16→  const [error, setError] = useState<string | null>(null);
    17→  const [search, setSearch] = useState(searchParams.get('search') || '');
    18→  const [page, setPage] = useState(1);
    19→  const [total, setTotal] = useState(0);
    20→  const [editingId, setEditingId] = useState<string | null>(null);
    21→  const [showModal, setShowModal] = useState(false);
    22→  const [editData, setEditData] = useState<Partial<Provider>>({});
    23→  const [mock, setMock] = useState(searchParams.get('mock') === 'true');
    24→  const [togglingId, setTogglingId] = useState<string | null>(null);
    25→  const [showDeleteModal, setShowDeleteModal] = useState(false);
    26→  const [pendingDeleteId, setPendingDeleteId] = useState<string | null>(null);
    27→  const [rememberChoice, setRememberChoice] = useState(false);
    28→
    29→  const fetchProviders = useCallback(async () => {
    30→    try {
    31→      setError(null);
    32→      setLoading(true);
    33→      const data = await getProviders({ page, limit: LIMIT, search: search || undefined });
    34→      console.log('Fetched data from API:', data); // Debug: Log full response
    35→      console.log('Setting providers to data.items:', data.items, 'Type:', typeof data.items, 'Is array:', Array.isArray(data.items)); // Debug: Log items specifically
    36→      if (!Array.isArray(data.items)) {
    37→        console.error('API response items is not an array:', data.items);
    38→        setProviders([]);
    39→        setTotal(0);
    40→        return;
    41→      }
    42→      setProviders(data.items);
    43→      setTotal(data.total);
    44→    } catch (err) {
    45→      setError(err instanceof Error ? err.message : 'Failed to fetch providers');
    46→      setProviders([]);
    47→      setTotal(0);
    48→    } finally {
    49→      setLoading(false);
    50→    }
    51→  }, [page, search, mock]);
    52→
    53→  useEffect(() => {
    54→    fetchProviders();
    55→  }, [fetchProviders]);
    56→
    57→  const handleCreate = async (data: Omit<Provider, 'id' | 'createdAt'>) => {
    58→    try {
    59→      await createProvider(data);
    60→      fetchProviders();
    61→      setShowModal(false);
    62→    } catch (err) {
    63→      setError(err instanceof Error ? err.message : 'Failed to create provider');
    64→    }
    65→  };
    66→
    67→  const handleUpdate = async (data: Partial<Provider>) => {
    68→    if (!editingId) return;
    69→    try {
    70→      await updateProvider(editingId, data);
    71→      fetchProviders();
    72→      setEditingId(null);
    73→      setShowModal(false);
    74→    } catch (err) {
    75→      setError(err instanceof Error ? err.message : 'Failed to update provider');
    76→    }
    77→  };
    78→
    79→  const handleToggle = useCallback(async (id: string) => {
    80→    if (togglingId === id) return;
    81→
    82→    const provider = providers.find(p => p.id === id);
    83→    if (!provider) return;
    84→
    85→    const currentActive = provider.active;
    86→    const newActive = !currentActive;
    87→
    88→    console.log(`Toggling provider ${id} to ${newActive ? 'active' : 'inactive'}`); // Debug log
    89→
    90→    setTogglingId(id);
    91→
    92→    try {
    93→      // Optimistic update
    94→      setProviders(prev => prev.map(p => p.id === id ? { ...p, active: newActive } : p));
    95→
    96→      await updateProvider(id, { active: newActive });
    97→
    98→      // Refetch to sync
    99→      await fetchProviders();
   100→    } catch (err) {
   101→      // Revert on error
   102→      setProviders(prev => prev.map(p => p.id === id ? { ...p, active: currentActive } : p));
   103→      setError(err instanceof Error ? err.message : 'Failed to toggle provider status');
   104→    } finally {
   105→      setTogglingId(null);
   106→    }
   107→  }, [providers, togglingId, updateProvider, fetchProviders]);
   108→
   109→
   110→  const handleDelete = async (id: string) => {
   111→    setPendingDeleteId(id);
   112→    setShowDeleteModal(true);
   113→  };
   114→
   115→  const handleConfirmDelete = async () => {
   116→    if (!pendingDeleteId) return;
   117→    try {
   118→      await deleteProvider(pendingDeleteId);
   119→      fetchProviders();
   120→      if (rememberChoice) {
   121→        sessionStorage.setItem('deleteConfirmed', 'true');
   122→      }
   123→    } catch (err) {
   124→      setError(err instanceof Error ? err.message : 'Failed to delete provider');
   125→    } finally {
   126→      setShowDeleteModal(false);
   127→      setPendingDeleteId(null);
   128→      setRememberChoice(false);
   129→    }
   130→  };
   131→
   132→  const handleRememberChange = (checked: boolean) => {
   133→    setRememberChoice(checked);
   134→  };
   135→
   136→  const openEdit = async (id: string) => {
   137→    try {
   138→      const provider = await getProvider(id);
   139→      setEditData(provider);
   140→      setEditingId(id);
   141→      setShowModal(true);
   142→    } catch (err) {
   143→      setError(err instanceof Error ? err.message : 'Failed to fetch provider');
   144→    }
   145→  };
   146→
   147→  return (
   148→    <div className="space-y-4">
   149→      <Card>
   150→        <CardHeader>
   151→          <div className="flex justify-between items-center">
   152→            <CardTitle className="flex items-center gap-2">
   153→              <Users2 className="w-5 h-5" />
   154→              Providers Management
   155→            </CardTitle>
   156→            <button onClick={() => setShowModal(true)} className="flex items-center gap-1 bg-primary text-primary-foreground px-4 py-2 rounded hover:bg-primary/90">
   157→              <Plus className="w-4 h-4" />
   158→              Add Provider
   159→            </button>
   160→          </div>
   161→        </CardHeader>
   162→        <CardContent>
   163→          {loading && (
   164→            <div className="p-8 flex items-center justify-center">
   165→              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mr-2"></div>
   166→              <span>Loading providers...</span>
   167→            </div>
   168→          )}
   169→          {!loading && (
   170→            <>
   171→              <div className="flex gap-2 mb-4">
   172→                <input
   173→                  type="text"
   174→                  placeholder="Search providers..."
   175→                  value={search}
   176→                  onChange={(e) => setSearch(e.target.value)}
   177→                  className="flex-1 px-3 py-2 border border-input rounded-md bg-background text-foreground placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:border-input"
   178→                />
   179→                <label className="flex items-center gap-2">
   180→                  <input type="checkbox" checked={mock} onChange={(e) => setMock((e.target as HTMLInputElement).checked)} />
   181→                  Mock Mode
   182→                </label>
   183→              </div>
   184→              <div className="space-y-2">
   185→                <div className="overflow-x-auto">
   186→                  <table className="w-full">
   187→                    <thead>
   188→                      <tr className="border-b border-border">
   189→                        <th className="text-left p-3 w-12">Logo</th>
   190→                        <th className="text-left p-3">Name</th>
   191→                        <th className="text-left p-3">Type</th>
   192→                        <th className="text-left p-3">Status</th>
   193→                        <th className="text-left p-3">Created</th>
   194→                        <th className="text-right p-3">Actions</th>
   195→                      </tr>
   196→                    </thead>
   197→                    <tbody>
   198→                      {providers.length === 0 ? (
   199→                        <tr>
   200→                          <td colSpan={6} className="p-4 text-center text-muted-foreground">
   201→                            No providers found. Add one to get started!
   202→                          </td>
   203→                        </tr>
   204→                      ) : (
   205→                        providers.map((provider) => (
   206→                          <tr key={provider.id} className="border-b border-border hover:bg-accent">
   207→                            <td className="p-3 w-12">
   208→                              {provider.logoUrl ? (
   209→                                <img src={provider.logoUrl} alt={provider.name} className="w-8 h-8 rounded object-cover" onError={(e) => { e.currentTarget.src = '/placeholder-logo.png'; }} />
   210→                              ) : (
   211→                                <div className="w-8 h-8 bg-gray-200 rounded flex items-center justify-center">
   212→                                  <span className="text-gray-500 text-xs">{provider.name.charAt(0).toUpperCase()}</span>
   213→                                </div>
   214→                              )}
   215→                            </td>
   216→                            <td className="p-3">{provider.name}</td>
   217→                            <td className="p-3">
   218→                              <span className={cn('px-2 py-1 rounded text-xs', provider.active ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800')}>
   219→                                {provider.type}
   220→                              </span>
   221→                            </td>
   222→                            <td className="p-3">
   223→                              <span className={cn('px-2 py-1 rounded text-xs', provider.active ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800')}>
   224→                                {provider.active ? 'Active' : 'Inactive'}
   225→                              </span>
   226→                            </td>
   227→                            <td className="p-3">{new Date(provider.createdAt).toLocaleDateString()}</td>
   228→                            <td className="p-3 text-right">
   229→                              <Switch
   230→                                checked={provider.active}
   231→                                onCheckedChange={(checked) => {
   232→                                  console.log(`Switch clicked for ${provider.id}, checked: ${checked}`); // Debug log
   233→                                  handleToggle(provider.id);
   234→                                }}
   235→                                disabled={togglingId === provider.id}
   236→                                className={cn(
   237→                                  'data-[state=checked]:bg-green-600 data-[state=unchecked]:bg-red-600',
   238→                                  'w-10 h-5 mr-2'
   239→                                )}
   240→                              />
   241→                              <button onClick={() => openEdit(provider.id)} className="p-1 text-blue-600 hover:text-blue-800" title="Edit provider details">
   242→                                <Edit className="w-4 h-4" />
   243→                              </button>
   244→                              <button onClick={() => handleDelete(provider.id)} className="p-1 text-red-600 hover:text-red-800" title="Delete provider">
   245→                                <Trash2 className="w-4 h-4" />
   246→                              </button>
   247→                            </td>
   248→                          </tr>
   249→                        ))
   250→                      )}
   251→                    </tbody>
   252→                  </table>
   253→                </div>
   254→                {providers.length === 0 && !error && (
   255→                  <p className="text-center text-muted-foreground py-8">No providers found. Add one to get started!</p>
   256→                )}
   257→              </div>
   258→              <div className="flex justify-between mt-4">
   259→                <button onClick={() => setPage(page - 1)} disabled={page === 1 || loading} className="px-4 py-2 bg-primary text-primary-foreground rounded disabled:opacity-50">
   260→                  Previous
   261→                </button>
   262→                <span>{page} of {Math.ceil(total / LIMIT)}</span>
   263→                <button onClick={() => setPage(page + 1)} disabled={providers.length < LIMIT || loading} className="px-4 py-2 bg-primary text-primary-foreground rounded disabled:opacity-50">
   264→                  Next
   265→                </button>
   266→              </div>
   267→              {error && (
   268→                <div className="p-4 bg-destructive/10 border border-destructive/30 rounded-md text-destructive text-sm mt-4 flex items-center justify-between">
   269→                  <span>{error}</span>
   270→                  <button
   271→                    onClick={fetchProviders}
   272→                    disabled={loading}
   273→                    className="px-3 py-1 bg-primary text-primary-foreground rounded text-sm disabled:opacity-50"
   274→                  >
   275→                    Retry
   276→                  </button>
   277→                </div>
   278→              )}
   279→            </>
   280→          )}
   281→        </CardContent>
   282→      </Card>
   283→
   284→      {showModal && (
   285→        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
   286→          <div className="w-full max-w-2xl bg-white rounded-lg shadow-xl">
   287→            <div className="p-6 border-b">
   288→              <h2 className="text-xl font-semibold">{editingId ? 'Edit Provider' : 'Add New Provider'}</h2>
   289→              <p className="text-sm text-gray-600 mt-1">Set up a provider to import and manage your proxy sources. Choose a type that matches your setup. We'll guide you through the configuration.</p>
   290→            </div>
   291→            <form onSubmit={async (e) => {
   292→              e.preventDefault();
   293→              const formData = new FormData(e.currentTarget);
   294→              const data = {
   295→                name: formData.get('name') as string,
   296→                type: (formData.get('type') as 'api' | 'file' | 'manual'),
   297→                logoUrl: formData.get('logoUrl') as string,
   298→                config: formData.get('config') ? JSON.parse(formData.get('config') as string) : {},
   299→                active: formData.get('active') === 'on',
   300→              };
   301→              try {
   302→                editingId ? await handleUpdate(data) : await handleCreate(data);
   303→              } catch (err) {
   304→                setError(err instanceof Error ? err.message : 'Failed to save provider');
   305→              }
   306→            }} className="p-6 space-y-6">
   307→              <div className="grid md:grid-cols-2 gap-6">
   308→                <div>
   309→                  <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-2">
   310→                    Provider Name <span className="text-red-500">*</span>
   311→                  </label>
   312→                  <input
   313→                    id="name"
   314→                    type="text"
   315→                    name="name"
   316→                    defaultValue={editData.name}
   317→                    required
   318→                    placeholder="e.g., Bright Data"
   319→                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors"
   320→                  />
   321→                </div>
   322→                <div>
   323→                  <label htmlFor="logoUrl" className="block text-sm font-medium text-gray-700 mb-2">
   324→                    Logo URL (Optional)
   325→                  </label>
   326→                  <input
   327→                    id="logoUrl"
   328→                    type="url"
   329→                    name="logoUrl"
   330→                    defaultValue={editData.logoUrl || ''}
   331→                    placeholder="https://example.com/logo.png"
   332→                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors"
   333→                  />
   334→                </div>
   335→              </div>
   336→
   337→              <div>
   338→                <label htmlFor="type" className="block text-sm font-medium text-gray-700 mb-2">
   339→                  Provider Type <span className="text-red-500">*</span>
   340→                </label>
   341→                <select
   342→                  id="type"
   343→                  name="type"
   344→                  defaultValue={editData.type || 'api'}
   345→                  required
   346→                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors"
   347→                >
   348→                  <option value="api">
   349→                    API (Automated)
   350→                  </option>
   351→                  <option value="file">
   352→                    File Upload
   353→                  </option>
   354→                  <option value="manual">
   355→                    Manual Entry
   356→                  </option>
   357→                </select>
   358→              </div>
   359→
   360→              <div>
   361→                <label className="block text-sm font-medium text-gray-700 mb-4">
   362→                  Configuration
   363→                </label>
   364→                <div className="space-y-2 mb-2">
   365→                  <div className="flex items-center gap-2 text-xs text-gray-500 bg-blue-50 p-3 rounded-md border border-blue-100">
   366→                    <svg className="w-4 h-4 text-blue-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
   367→                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.228 9c.549-1.165 2.03-2 3.772-2 2.21 0 4 1.343 4 3 0 1.4-1.278 2.575-3.006 2.907-.542.104-.994.54-.994 1.093m0 3h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
   368→                    </svg>
   369→                    <div>
   370→                      <p>JSON Configuration</p>
   371→                      <p className="mb-0">Configure API keys, endpoints, file paths, or other settings for your provider type. See <a href="https://docs.proxyhub.com/providers/config" target="_blank" className="text-blue-600 hover:underline">documentation</a> for type-specific examples.</p>
   372→                    </div>
   373→                  </div>
   374→                </div>
   375→                <textarea
   376→                  name="config"
   377→                  defaultValue={JSON.stringify(editData.config, null, 2)}
   378→                  rows={8}
   379→                  placeholder='{
   380→  "apiKey": "your-api-key",
   381→  "endpoint": "https://api.example.com/proxies",
   382→  "authType": "bearer"
   383→}'
   384→                  className="w-full px-3 py-2 border border-gray-300 rounded-md font-mono text-sm bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-vertical transition-colors"
   385→                  title="Enter JSON configuration. Use templates from documentation based on your provider type. Validate with a JSON linter if needed."
   386→                />
   387→                <div className="text-xs text-gray-500 mt-1">
   388→                  Tip: Start with the example config from your provider's documentation. Common fields: apiKey, endpoint, username, password, filePath.
   389→                </div>
   390→              </div>
   391→
   392→              <div className="flex items-center gap-3">
   393→                <label className="flex items-center gap-2">
   394→                  <input type="checkbox" name="active" defaultChecked={editData.active !== false} className="rounded" />
   395→                  <span className="text-sm">Active</span>
   396→                </label>
   397→                <div className="ml-auto flex gap-3">
   398→                  <button
