     1→import type { Provider, Proxy } from '../types';
     2→
     3→export type { Provider, Proxy };
     4→
     5→const API_BASE = import.meta.env.VITE_API_URL || 'http://localhost:8080';
     6→
     7→interface PaginationResponse<T> {
     8→  items: T[];
     9→  total: number;
    10→  page: number;
    11→  limit: number;
    12→}
    13→
    14→interface ApiError {
    15→  message: string;
    16→  status: number;
    17→}
    18→
    19→export async function apiRequest<T>(url: string, options: RequestInit = {}): Promise<T> {
    20→  const fullUrl = `${API_BASE}${url}`;
    21→  const response = await fetch(fullUrl, {
    22→    headers: {
    23→      'Content-Type': 'application/json',
    24→      ...options.headers,
    25→    },
    26→    ...options,
    27→  });
    28→
    29→  if (!response.ok) {
    30→    const error: ApiError = await response.json().catch(() => ({ message: 'Network error', status: response.status }));
    31→    throw new Error(`${error.status}: ${error.message}`);
    32→  }
    33→
    34→  return response.json();
    35→}
    36→
    37→export async function getProxies(params: {
    38→  page?: number;
    39→  limit?: number;
    40→  pool?: string;
    41→  providerId?: string;
    42→  bbox?: string; // minLon,minLat,maxLon,maxLat
    43→} = {}): Promise<PaginationResponse<Proxy>> {
    44→  const query = new URLSearchParams({
    45→    page: (params.page || 1).toString(),
    46→    limit: (params.limit || 10).toString(),
    47→    ...(params.pool && { pool: params.pool }),
    48→    ...(params.providerId && { providerId: params.providerId }),
    49→    ...(params.bbox && { bbox: params.bbox }),
    50→  });
    51→  return apiRequest(`/v1/proxies?${query}`);
    52→}
    53→
    54→export async function getProviders(params: {
    55→  page?: number;
    56→  limit?: number;
    57→  search?: string;
    58→} = {}): Promise<PaginationResponse<Provider>> {
    59→  const query = new URLSearchParams({
    60→    page: (params.page || 1).toString(),
    61→    limit: (params.limit || 10).toString(),
    62→    ...(params.search && { search: params.search }),
    63→  });
    64→  return apiRequest(`/v1/providers?${query}`);
    65→}
    66→
    67→export async function createProvider(provider: Omit<Provider, 'id' | 'createdAt'>): Promise<Provider> {
    68→  return apiRequest('/v1/providers', {
    69→    method: 'POST',
    70→    body: JSON.stringify(provider),
    71→  });
    72→}
    73→
    74→export async function updateProvider(id: string, provider: Partial<Provider>): Promise<Provider> {
    75→  return apiRequest(`/v1/providers/${id}`, {
    76→    method: 'PATCH',
    77→    body: JSON.stringify(provider),
    78→  });
    79→}
    80→
    81→export async function deleteProvider(id: string): Promise<void> {
    82→  await apiRequest(`/v1/providers/${id}`, { method: 'DELETE' });
    83→}
    84→
    85→export async function getProvider(id: string): Promise<Provider> {
    86→  return apiRequest(`/v1/providers/${id}`);
    87→}
    88→
    89→export async function getProxiesCount(): Promise<{ count: number }> {
    90→  return apiRequest('/v1/proxies/count');
    91→}
    92→
    93→export async function getProxiesStats(): Promise<{ avgScore: number }> {
    94→  return apiRequest('/v1/proxies/stats');
    95→}
    96→
    97→export async function getPoolsStats(): Promise<{ total: number }> {
    98→  return apiRequest('/v1/pools/stats');
    99→}
   100→
   101→export async function getUsageSummary(): Promise<{ total: number }> {
   102→  return apiRequest('/v1/usage/summary');
   103→}
   104→
   105→export async function getUsageStats(): Promise<UsageData[]> {
   106→  return apiRequest('/v1/usage/stats');
   107→}
   108→
   109→export interface CreateProxy {
   110→  host: string;
   111→  port: number;
   112→  username?: string;
   113→  password?: string;
   114→  protocol?: string;
   115→  pool?: string;
   116→  providerId?: string;
   117→  tags?: string[];
   118→  meta?: any;
   119→  disabled?: boolean;
   120→}
   121→
   122→export interface UpdateProxy {
   123→  pool?: string;
   124→  providerId?: string;
   125→  tags?: string[];
   126→  meta?: any;
   127→  disabled?: boolean;
   128→}
   129→
   130→export interface Lease {
   131→  leaseId: string;
   132→  proxy: string; // http://user:pass@host:port
   133→  protocol: string;
   134→  expiresAt: string;
   135→  meta: {
   136→    providerId?: string;
   137→    score: number;
   138→    country?: string;
   139→    sticky: boolean;
   140→  };
   141→}
