    45→      setProviders(data.items);
    46→      setTotal(data.total);
    47→    } catch (err) {
    48→      setError(err instanceof Error ? err.message : 'Failed to fetch providers');
    49→      setProviders([]);
    50→      setTotal(0);
    51→    } finally {
    52→      setLoading(false);
    53→    }
    54→  }, [page, search, mock]);
