    71→    if (!editingId) return;
    72→    try {
    73→      await updateProvider(editingId, data);
    74→      fetchProviders();
    75→      setEditingId(null);
    76→      setShowModal(false);
    77→    } catch (err) {
    78→      setError(err instanceof Error ? err.message : 'Failed to update provider');
    79→    }
    80→  };
    81→
