const getPlaceholder = (type: 'api' | 'file' | 'manual') => {
  switch (type) {
    case 'api':
      return `{
"kind": "iproyal",
"access_token": "YOUR_X_ACCESS_TOKEN",
"list_endpoint": "https://apid.iproyal.com/v1/reseller/datacenter/proxies",
"default_pool": "default"
}`;
    case 'file':
      return `{
"filePath": "/path/to/proxies.json",
"format": "json",
"pools": {
  "default": "general"
}
}`;
