   236→    <div className="space-y-4">
   237→      <Card>
   238→        <CardHeader>
   239→          <div className="flex justify-between items-center">
   240→            <CardTitle className="flex items-center gap-2">
   241→              <Users2 className="w-5 h-5" />
   242→              Providers Management
   243→            </CardTitle>
   244→            <button onClick={() => {
   245→              setEditingId(null);
   246→              setEditData({});
   247→              setSelectedType('api');
   248→              setConfig('');
   249→              setFileContent(null);
   250→              setShowModal(true);
   251→            }} className="flex items-center gap-1 bg-primary text-primary-foreground px-4 py-2 rounded hover:bg-primary/90">
   252→              <Plus className="w-4 h-4" />
   253→              Add Provider
   254→            </button>
   255→          </div>
   256→        </CardHeader>
   257→        <CardContent>
   258→          {loading && (
   259→            <div className="p-8 flex items-center justify-center">
   260→              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mr-2"></div>
   261→              <span>Loading providers...</span>
   262→            </div>
   263→          )}
   264→          {error && (
   265→            <div className="p-4 bg-destructive/10 border border-destructive/30 rounded-md text-destructive text-sm mb-4">
   266→              {error}
   267→            </div>
   268→          )}
   269→          {!loading && (
   270→            <>
   271→              <div className="flex gap-2 mb-4">
   272→                <input
   273→                  type="text"
   274→                  placeholder="Search providers..."
   275→                  value={search}
   276→                  onChange={(e) => setSearch(e.target.value)}
   277→                  className="flex-1 px-3 py-2 border border-input rounded-md bg-background text-foreground placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:border-input"
   278→                />
   279→                <label className="flex items-center gap-2">
   280→                  <input type="checkbox" checked={mock} onChange={(e) => setMock(e.target.checked)} />
   281→                  Mock Mode
   282→                </label>
   283→              </div>
   284→              <div className="space-y-2">
   285→                <div className="overflow-x-auto">
   286→                  <table className="w-full">
   287→                    <thead>
   288→                      <tr className="border-b border-border">
   289→                        <th className="text-left p-3 w-12">Logo</th>
   290→                        <th className="text-left p-3">Name</th>
   291→                        <th className="text-left p-3">Type</th>
   292→                        <th className="text-left p-3">Status</th>
   293→                        <th className="text-left p-3">Created</th>
   294→                        <th className="text-right p-3">Actions</th>
   295→                      </tr>
   296→                    </thead>
   297→                    <tbody>
   298→                      {providers.length === 0 ? (
   299→                        <tr>
   300→                          <td colSpan={6} className="p-4 text-center text-muted-foreground">
   301→                            No providers found. Add one to get started!
   302→                          </td>
   303→                        </tr>
   304→                      ) : (
   305→                        providers.map((provider) => (
   306→                          <tr key={provider.id} className="border-b border-border hover:bg-accent">
   307→                            <td className="p-3 w-12">
   308→                              {provider.logoUrl ? (
   309→                                <img src={provider.logoUrl} alt={provider.name} className="w-8 h-8 rounded object-cover" onError={(e) => { e.currentTarget.src = '/placeholder-logo.png'; }} />
   310→                              ) : (
   311→                                <div className="w-8 h-8 bg-gray-200 rounded flex items-center justify-center">
   312→                                  <span className="text-gray-500 text-xs">{provider.name.charAt(0).toUpperCase()}</span>
   313→                                </div>
   314→                              )}
   315→                            </td>
   316→                            <td className="p-3">{provider.name}</td>
   317→                            <td className="p-3">
   318→                              <span className={cn('px-2 py-1 rounded text-xs', provider.active ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800')}>
   319→                                {provider.type}
   320→                              </span>
   321→                            </td>
   322→                            <td className="p-3">
   323→                              <span className={cn('px-2 py-1 rounded text-xs', provider.active ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800')}>
   324→                                {provider.active ? 'Active' : 'Inactive'}
   325→                              </span>
   326→                            </td>
   327→                            <td className="p-3">{new Date(provider.createdAt).toLocaleDateString()}</td>
   328→                            <td className="p-3 text-right">
   329→                              <Switch
   330→                                checked={provider.active}
   331→                                onCheckedChange={(checked) => handleToggle(provider.id)}
   332→                                disabled={togglingId === provider.id}
   333→                                className={cn('data-[state=checked]:bg-green-600 data-[state=unchecked]:bg-red-600', 'w-10 h-5 mr-2')}
   334→                              />
   335→                              <button onClick={() => openEdit(provider.id)} className="p-1 text-blue-600 hover:text-blue-800" title="Edit provider details">
   336→                                <Edit className="w-4 h-4" />
   337→                              </button>
   338→                              <button onClick={() => handleDelete(provider.id)} className="p-1 text-red-600 hover:text-red-800" title="Delete provider">
   339→                                <Trash2 className="w-4 h-4" />
   340→                              </button>
   341→                            </td>
   342→                          </tr>
   343→                        ))
   344→                      )}
   345→                    </tbody>
   346→                  </table>
   347→                </div>
   348→                {providers.length === 0 && !error && (
   349→                  <p className="text-center text-muted-foreground py-8">No providers found. Add one to get started!</p>
   350→                )}
   351→              </div>
   352→              <div className="flex justify-between mt-4">
   353→                <button onClick={() => setPage(page - 1)} disabled={page === 1 || loading} className="px-4 py-2 bg-primary text-primary-foreground rounded disabled:opacity-50">
   354→                  Previous
   355→                </button>
   356→                <span>{page} of {Math.ceil(total / LIMIT)}</span>
   357→                <button onClick={() => setPage(page + 1)} disabled={providers.length < LIMIT || loading} className="px-4 py-2 bg-primary text-primary-foreground rounded disabled:opacity-50">
   358→                  Next
   359→                </button>
   360→              </div>
   361→              {error && (
   362→                <div className="p-4 bg-destructive/10 border border-destructive/30 rounded-md text-destructive text-sm mt-4 flex items-center justify-between">
   363→                  <span>{error}</span>
   364→                  <button onClick={fetchProviders} disabled={loading} className="px-3 py-1 bg-primary text-primary-foreground rounded text-sm disabled:opacity-50">
   365→                    Retry
   366→                  </button>
   367→                </div>
   368→              )}
   369→            </>
   370→          )}
   371→        </CardContent>
   372→      </Card>
   373→
   374→      {showModal && (
   375→        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
   376→          <div className="w-full max-w-2xl bg-white rounded-lg shadow-xl">
   377→            <div className="p-6 border-b">
   378→              <h2 className="text-xl font-semibold">{editingId ? 'Edit Provider' : 'Add New Provider'}</h2>
   379→              <p className="text-sm text-gray-600 mt-1">Set up a provider to import and manage your proxy sources. Choose a type that matches your setup. We'll guide you through the configuration.</p>
   380→            </div>
   381→            <form onSubmit={async (e) => {
   382→              e.preventDefault();
   383→              try {
   384→                JSON.parse(config); // Validate JSON
   385→              } catch {
   386→                setError('Invalid JSON in configuration. Please check and correct.');
   387→                return;
   388→              }
   389→              const formData = new FormData(e.currentTarget);
   390→              const finalConfig = fileContent ? {
   391→                ...JSON.parse(config),
   392→                file: fileContent.name,
   393→                uploaded: true
   394→              } : JSON.parse(config);
   395→              const data = {
   396→                name: formData.get('name') as string,
   397→                type: selectedType,
   398→                logoUrl: formData.get('logoUrl') as string,
   399→                config: finalConfig,
   400→                active: formData.get('active') === 'on',
   401→              };
   402→              try {
   403→                editingId ? await handleUpdate(data) : await handleCreate(data);
   404→              } catch (err) {
   405→                setError(err instanceof Error ? err.message : 'Failed to save provider');
   406→              }
   407→            }} className="p-6 space-y-6">
   408→              <div className="grid md:grid-cols-2 gap-6">
   409→                <div>
   410→                  <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-2">
   411→                    Provider Name <span className="text-red-500">*</span>
   412→                  </label>
   413→                  <input
   414→                    id="name"
   415→                    type="text"
   416→                    name="name"
   417→                    defaultValue={editData.name}
   418→                    required
   419→                    placeholder="e.g., Bright Data"
   420→                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors"
   421→                  />
   422→                </div>
   423→                <div>
   424→                  <label htmlFor="logoUrl" className="block text-sm font-medium text-gray-700 mb-2">
   425→                    Logo URL (Optional)
   426→                  </label>
   427→                  <input
   428→                    id="logoUrl"
   429→                    type="url"
   430→                    name="logoUrl"
   431→                    defaultValue={editData.logoUrl || ''}
   432→                    placeholder="https://example.com/logo.png"
   433→                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors"
   434→                  />
   435→                </div>
   436→              </div>
   437→
   438→              <div>
   439→                <label htmlFor="type" className="block text-sm font-medium text-gray-700 mb-2">
   440→                  Provider Type <span className="text-red-500">*</span>
   441→                </label>
   442→                <select
   443→                  id="type"
   444→                  name="type"
   445→                  value={selectedType}
   446→                  onChange={(e) => {
   447→                    const value = e.target.value as 'api' | 'file' | 'manual';
   448→                    setSelectedType(value);
   449→                  }}
   450→                  required
   451→                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors"
   452→                >
   453→                  <option value="api">
   454→                    API (Automated)
   455→                  </option>
   456→                  <option value="file">
   457→                    File Upload
   458→                  </option>
   459→                  <option value="manual">
   460→                    Manual Entry
   461→                  </option>
   462→                </select>
   463→              </div>
   464→              <div>
   465→                <label className="block text-sm font-medium text-gray-700 mb-4">
   466→                  Configuration
   467→                </label>
   468→                <div className="space-y-2 mb-2">
   469→                  <div className="flex items-center gap-2 text-xs text-gray-500 bg-blue-50 p-3 rounded-md border border-blue-100">
   470→                    <svg className="w-4 h-4 text-blue-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
   471→                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.228 9c.549-1.165 2.03-2 3.772-2 2.21 0 4 1.343 4 3 0 1.4-1.278 2.575-3.006 2.907-.542.104-.994.54-.994 1.093m0 3h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
   472→                    </svg>
   473→                    <div>
   474→                      <p>JSON Configuration</p>
   475→                      <p className="mb-0">Configure API keys, endpoints, file paths, or other settings for your provider type. See <a href="https://docs.proxyhub.com/providers/config" target="_blank" className="text-blue-600 hover:underline">documentation</a> for type-specific examples.</p>
   476→                    </div>
   477→                  </div>
   478→                </div>
   479→                <textarea
   480→                  name="config"
   481→                  value={config}
   482→                  onChange={(e) => setConfig(e.target.value)}
   483→                  rows={8}
   484→                  placeholder={getPlaceholder(selectedType)}
   485→                  className="w-full px-3 py-2 border border-gray-300 rounded-md font-mono text-sm bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-vertical transition-colors"
   486→                  title="Enter JSON configuration. Use the placeholder example for your selected provider type. Validate with a JSON linter if needed."
   487→                />
   488→                {selectedType === 'file' && (
   489→                  <div className="mt-2">
   490→                    <label htmlFor="configFile" className="block text-sm font-medium text-gray-700 mb-1">
   491→                      Upload Config File (JSON/CSV)
   492→                    </label>
   493→                    <input
   494→                      id="configFile"
   495→                      type="file"
   496→                      accept=".json,.csv"
   497→                      onChange={async (e) => {
   498→                        const file = e.target.files?.[0];
   499→                        if (file) {
   500→                          setFileContent(file);
   501→                          const reader = new FileReader();
   502→                          reader.onload = (event) => {
   503→                            const content = event.target?.result as string;
   504→                            try {
   505→                              const parsed = JSON.parse(content); // Assume JSON, or parse CSV manually if needed
   506→                              setConfig(JSON.stringify({ source: 'uploaded', data: parsed }, null, 2));
   507→                            } catch {
   508→                              setConfig(JSON.stringify({ source: 'uploaded', content: content }, null, 2));
   509→                            }
   510→                          };
   511→                          reader.readAsText(file);
   512→                        }
   513→                      }}
   514→                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
   515→                    />
   516→                  </div>
   517→                )}
   518→                <div className="text-xs text-gray-500 mt-1">
   519→                  Tip: Start with the example config from your provider's documentation. Common fields: apiKey, endpoint, username, password, filePath.
   520→                </div>
