                ...JSON.parse(config),
                file: fileContent.name,
                uploaded: true
              } : JSON.parse(config);
              const data = {
                name: formData.get('name') as string,
                type: selectedType,
                logoUrl: formData.get('logoUrl') as string,
                config: finalConfig,
                active: formData.get('active') === 'on',
              };
