                editingId ? await handleUpdate(data) : await handleCreate(data);
              } catch (err) {
                setError(err instanceof Error ? err.message : 'Failed to save provider');
              }
            }} className="p-6 space-y-6">
              <div className="grid md:grid-cols-2 gap-6">
