   398→                                <Button variant="ghost" size="sm" onClick={() => handleTest(proxy.id)} className="hover:bg-green-100 dark:hover:bg-green-900 hover:scale-110 transition-all">
   399→                                  <Zap className="w-4 h-4" />
   400→                                </Button>
   401→                              </TooltipTrigger>
   402→                              <TooltipContent>Test proxy connectivity</TooltipContent>
   403→                            </Tooltip>
