   405→                          <Button variant="ghost" size="sm" onClick={() => openEdit(proxy.id)} className="hover:bg-blue-100 dark:hover:bg-blue-900 hover:scale-110 transition-all">
   406→                            <Edit className="w-4 h-4" />
   407→                          </Button>
