   447→              {error}
   448→            </div>
   449→          )}
   450→        </CardContent>
   451→      </Card>
   452→
   453→      {/* Create/Edit Modal */}
   454→      {showModal && (
   455→        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50" onClick={() => setShowModal(false)}>
   456→          <div className="bg-background rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto" onClick={e => e.stopPropagation()}>
   457→            <div className="p-6 border-b">
   458→              <h2 className="text-xl font-semibold">
   459→                {editingId ? 'Edit Proxy' : 'Add New Proxy'}
   460→              </h2>
