   462→                Configure proxy details. For authentication, enter credentials if required.
   463→              </p>
   464→            </div>
   465→            <form
   466→              onSubmit={async (e) => {
   467→                e.preventDefault();
   468→                const formData = new FormData(e.currentTarget);
   469→                const data: any = {
   470→                  host: formData.get('host') as string,
   471→                  port: parseInt(formData.get('port') as string),
   472→                  username: formData.get('username') as string || undefined,
   473→                  password: formData.get('password') as string || undefined,
