    34→
    35→export default function Proxies() {
    36→  const [proxies, setProxies] = useState<Proxy[]>([]);
    37→  const [loading, setLoading] = useState(true);
    38→  const [error, setError] = useState<string | null>(null);
    39→  const [search, setSearch] = useState('');
    40→  const [selectedPool, setSelectedPool] = useState('');
    41→  const [selectedProvider, setSelectedProvider] = useState('');
    42→  const [page, setPage] = useState(1);
    43→  const [total, setTotal] = useState(0);
    44→  const [showModal, setShowModal] = useState(false);
    45→  const [editingId, setEditingId] = useState<string | null>(null);
    46→  const [editData, setEditData] = useState<any>({});
    47→  const [showTestModal, setShowTestModal] = useState(false);
    48→  const [testingId, setTestingId] = useState<string | null>(null);
    49→  const [testResult, setTestResult] = useState<TestResult | null>(null);
