    51→  const [providers, setProviders] = useState<Provider[]>([]);
    52→  const [fetchingProviders, setFetchingProviders] = useState(false);
    53→  const [showDeleteModal, setShowDeleteModal] = useState(false);
    54→  const [pendingDeleteId, setPendingDeleteId] = useState<string | null>(null);
    55→  const [rememberChoice, setRememberChoice] = useState(false);
    56→
    57→  // Fetch providers for filter dropdown
    58→  useEffect(() => {
    59→    const fetchProviders = async () => {
    60→      setFetchingProviders(true);
