    63→        setProviders(data.items);
    64→      } catch (err) {
    65→        console.error('Failed to fetch providers:', err);
    66→      } finally {
    67→        setFetchingProviders(false);
    68→      }
    69→    };
    70→    fetchProviders();
    71→  }, []);
    72→
    73→  const fetchProxies = useCallback(async () => {
    74→    try {
    75→      setLoading(true);
    76→      setError(null);
    77→      const params: any = {
    78→        page,
    79→        limit: LIMIT,
    80→      };
    81→      if (search) params.search = search; // Assuming API supports search; if not, client-side
    82→      if (selectedPool) params.pool = selectedPool;
    83→      if (selectedProvider) params.providerId = selectedProvider;
    84→      const data = await getProxies(params);
    85→      setProxies(data.items);
    86→      setTotal(data.total);
    87→    } catch (err) {
    88→      setError(err instanceof Error ? err.message : 'Failed to fetch proxies');
    89→    } finally {
    90→      setLoading(false);
    91→    }
    92→  }, [page, search, selectedPool, selectedProvider]);
    93→
    94→  useEffect(() => {
    95→    fetchProxies();
    96→  }, [fetchProxies]);
    97→
    98→  const resetFilters = () => {
    99→    setSearch('');
   100→    setSelectedPool('');
   101→    setSelectedProvider('');
   102→    setPage(1);
   103→  };
   104→
   105→  const handleCreate = async (data: CreateProxy) => {
   106→    try {
   107→      await createProxy(data);
   108→      fetchProxies();
   109→      setShowModal(false);
   110→      setEditData({});
   111→    } catch (err) {
   112→      setError(err instanceof Error ? err.message : 'Failed to create proxy');
   113→    }
   114→  };
   115→
   116→  const handleUpdate = async (data: UpdateProxy) => {
   117→    if (!editingId) return;
   118→    try {
   119→      await updateProxy(editingId, data);
   120→      fetchProxies();
   121→      setEditingId(null);
   122→      setShowModal(false);
   123→      setEditData({});
   124→    } catch (err) {
   125→      setError(err instanceof Error ? err.message : 'Failed to update proxy');
   126→    }
   127→  };
   128→
   129→  const handleToggle = useCallback(async (id: string, currentDisabled: boolean) => {
   130→    if (togglingId === id) return;
   131→    setTogglingId(id);
   132→    try {
   133→      // Optimistic update
   134→      setProxies(prev => prev.map(p => p.id === id ? { ...p, disabled: !currentDisabled } : p));
   135→      await updateProxy(id, { disabled: !currentDisabled });
