   162→    } finally {
   163→      setShowDeleteModal(false);
   164→      setPendingDeleteId(null);
   165→      setRememberChoice(false);
   166→    }
   167→  };
   168→
   169→  const handleRememberChange = (checked: boolean) => {
   170→    setRememberChoice(checked);
   171→  };
   172→
   173→  const openEdit = async (id: string) => {
   174→    try {
   175→      const proxy = proxies.find(p => p.id === id) || await getProxy(id); // Fetch if not in list
   176→      if (proxy) {
   177→        console.log(proxy)
   178→        setEditData({
   179→          host: proxy.host,
   180→          port: proxy.port,
   181→          username: proxy.username,
   182→          password: proxy.password ? '*****' : '',
   183→          protocol: proxy.protocol,
   184→          pool: proxy.pool,
   185→          providerId: proxy.providerId,
   186→          tags: proxy.tags?.join(', '),
   187→          meta: JSON.stringify(proxy.meta, null, 2),
   188→          disabled: proxy.disabled,
   189→        });
   190→        setEditingId(id);
   191→        setShowModal(true);
   192→      }
   193→    } catch (err) {
   194→      setError('Failed to prepare edit');
   195→    }
   196→  };
   197→
   198→  const handleTest = async (id: string) => {
   199→    setTestingId(id);
   200→    setTestResult(null);
   201→    setShowTestModal(true);
   202→    try {
   203→      const proxy = proxies.find(p => p.id === id);
   204→      if (!proxy || !proxy.pool) throw new Error('Invalid proxy');
   205→      const leaseResponse = await issueLease({
   206→        project: 'admin-test',
   207→        pool: proxy.pool,
   208→        sticky: false,
   209→      });
   210→      if ('error' in leaseResponse) {
   211→        setTestResult({ success: false, error: leaseResponse.error, score: proxy.score });
   212→      } else {
   213→        // Simulate test: In real, fetch via proxy to external URL
   214→        // For now, assume success if lease issued
   215→        setTestResult({
   216→          success: true,
   217→          proxyString: leaseResponse.proxy,
   218→          score: leaseResponse.meta.score,
   219→        });
   220→        // Optionally release lease immediately
   221→        // await releaseLease(leaseResponse.leaseId, { status: 'ok' });
   222→      }
   223→    } catch (err) {
   224→      setTestResult({
   225→        success: false,
   226→        error: err instanceof Error ? err.message : 'Test failed',
   227→        score: proxies.find(p => p.id === id)?.score || 0,
   228→      });
   229→    } finally {
   230→      setTestingId(null);
   231→    }
   232→  };
   233→
   234→  if (loading) {
   235→    return (
   236→      <div className="p-8 flex items-center justify-center">
   237→        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
   238→        <span className="ml-2">Loading proxies...</span>
   239→      </div>
   240→    );
   241→  }
   242→
   243→  return (
   244→    <div className="space-y-4 p-4">
   245→      <Card>
   246→        <CardHeader>
   247→          <div className="flex justify-between items-center">
   248→            <CardTitle className="flex items-center gap-2">
   249→              <Globe className="w-5 h-5" />
   250→              Proxies Management
   251→              <TooltipProvider>
   252→                <Tooltip>
   253→                  <TooltipTrigger>
   254→                    <span className="text-muted-foreground ml-1">(?)</span>
   255→                  </TooltipTrigger>
   256→                  <TooltipContent>
   257→                    <p>Manage, test, and organize your uploaded proxies from various providers.</p>
   258→                  </TooltipContent>
   259→                </Tooltip>
   260→              </TooltipProvider>
   261→            </CardTitle>
   262→            <Button onClick={() => setShowModal(true)} className="flex items-center gap-1 hover:scale-105 transition-transform">
   263→              <Plus className="w-4 h-4" />
   264→              Add Proxy
   265→            </Button>
   266→          </div>
   267→        </CardHeader>
   268→        <CardContent>
   269→          {/* Filters and Search */}
   270→          <div className="flex flex-col md:flex-row gap-4 mb-4 p-4 bg-muted rounded-lg">
   271→            <div className="flex-1 flex gap-2">
   272→              <div className="relative flex-1">
   273→                <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-muted-foreground w-4 h-4" />
   274→                <input
   275→                  type="text"
   276→                  placeholder="Search proxies by host or tags..."
   277→                  value={search}
   278→                  onChange={(e) => setSearch(e.target.value)}
   279→                  className="w-full pl-10 pr-4 py-2 border border-input rounded-md bg-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
   280→                />
   281→              </div>
   282→            </div>
   283→            <div className="flex gap-2">
   284→              <select
   285→                value={selectedPool}
   286→                onChange={(e) => setSelectedPool(e.target.value)}
   287→                className="px-3 py-2 border border-input rounded-md bg-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
   288→              >
   289→                <option value="">All Pools</option>
   290→                <option value="residential">Residential</option>
   291→                <option value="isp">ISP</option>
   292→                <option value="datacenter">Datacenter</option>
   293→                <option value="mobile">Mobile</option>
   294→                <option value="web_unblocker">Web Unblocker</option>
   295→                <option value="test">Test</option>
   296→                {/* Dynamically from data if needed */}
   297→              </select>
   298→              <select
   299→                value={selectedProvider}
   300→                onChange={(e) => setSelectedProvider(e.target.value)}
   301→                disabled={fetchingProviders}
   302→                className="px-3 py-2 border border-input rounded-md bg-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring disabled:opacity-50"
   303→              >
   304→                <option value="">All Providers</option>
   305→                {providers.map(p => (
   306→                  <option key={p.id} value={p.id}>{p.name}</option>
   307→                ))}
   308→              </select>
   309→              <Button variant="outline" onClick={resetFilters} size="sm">
   310→                Clear
   311→              </Button>
   312→            </div>
   313→          </div>
   314→
   315→          {/* Table */}
   316→          <div className="overflow-x-auto">
   317→            <table className="w-full">
   318→              <thead>
   319→                <tr className="border-b border-border">
   320→                  <th className="text-left p-3">Host:Port</th>
   321→                  <th className="text-left p-3">Pool</th>
   322→                  <th className="text-left p-3">Provider</th>
   323→                  <th className="text-left p-3">Location</th>
   324→                  <th className="text-left p-3">Score</th>
   325→                  <th className="text-left p-3">Status</th>
   326→                  <th className="text-right p-3">Actions</th>
   327→                </tr>
   328→              </thead>
   329→              <tbody>
   330→                {proxies.length === 0 ? (
   331→                  <tr>
   332→                    <td colSpan={7} className="p-8 text-center text-muted-foreground">
   333→                      No proxies found. {search || selectedPool || selectedProvider ? 'Try adjusting filters.' : 'Add one above!'}
   334→                    </td>
   335→                  </tr>
   336→                ) : (
   337→                  proxies.map((proxy) => {
   338→                    const provider = providers.find(p => p.id === proxy.providerId);
   339→                    console.log('proxy:', proxy);
   340→                    return (
   341→                      <tr key={proxy.id} className="border-b border-border hover:bg-accent data-[state=hover]:scale-[1.01] transition-transform">
   342→                        <td className="p-3 font-mono">
   343→                          <TooltipProvider>
   344→                            <Tooltip>
   345→                              <TooltipTrigger>
   346→                                {proxy.host}:{proxy.port}
   347→                              </TooltipTrigger>
   348→                              <TooltipContent>
   349→                                <p>Protocol: {proxy.protocol || 'http'}</p>
   350→                                {proxy.username && <p>Auth: Username provided (masked)</p>}
   351→                              </TooltipContent>
   352→                            </Tooltip>
   353→                          </TooltipProvider>
   354→                        </td>
   355→                        <td className="p-3">
   356→                          <Badge variant="secondary">{proxy.pool || 'default'}</Badge>
   357→                        </td>
   358→                        <td className="p-3">
   359→                          {provider ? provider.name : 'Unknown'}
   360→                        </td>
   361→                        <td className="p-3">
   362→                          <TooltipProvider>
   363→                            <Tooltip>
   364→                              <TooltipTrigger>
   365→                                <div className="flex items-center gap-1">
   366→                                  <Globe className="w-4 h-4" />
   367→                                  {proxy.country}, {proxy.city}
   368→                                </div>
   369→                              </TooltipTrigger>
   370→                              <TooltipContent>
   371→                                <p>Region: {proxy.region}</p>
   372→                                {proxy.asn && <p>ASN: {proxy.asn} ({proxy.org})</p>}
   373→                              </TooltipContent>
   374→                            </Tooltip>
   375→                          </TooltipProvider>
   376→                        </td>
   377→                        <td className="p-3">
   378→                          <TooltipProvider>
   379→                            <Tooltip>
   380→                              <TooltipTrigger>
   381→                                <Badge variant={proxy.score > 80 ? 'default' : 'secondary'}>
   382→                                  {Math.round(proxy.score)}%
   383→                                </Badge>
   384→                              </TooltipTrigger>
   385→                              <TooltipContent>
   386→                                <p>Reliability score based on recent usage (higher is better).</p>
   387→                                <p>Failures: {proxy.failedCount}</p>
   388→                              </TooltipContent>
   389→                            </Tooltip>
   390→                          </TooltipProvider>
   391→                        </td>
   392→                        <td className="p-3">
   393→                          <Switch
   394→                            checked={!proxy.disabled}
   395→                            onCheckedChange={() => handleToggle(proxy.id, proxy.disabled)}
   396→                            disabled={togglingId === proxy.id}
   397→                            className="data-[state=checked]:bg-green-500 data-[state=unchecked]:bg-red-500 transition-colors"
   398→                          />
   399→                        </td>
   400→                        <td className="p-3 text-right space-x-1">
   401→                          <TooltipProvider>
   402→                            <Tooltip>
   403→                              <TooltipTrigger asChild>
   404→                                <Button variant="ghost" size="sm" onClick={() => handleTest(proxy.id)} className="hover:bg-green-100 dark:hover:bg-green-900 hover:scale-110 transition-all">
   405→                                  <Zap className="w-4 h-4" />
   406→                                </Button>
   407→                              </TooltipTrigger>
   408→                              <TooltipContent>Test proxy connectivity</TooltipContent>
   409→                            </Tooltip>
   410→                          </TooltipProvider>
   411→                          <Button variant="ghost" size="sm" onClick={() => openEdit(proxy.id)} className="hover:bg-blue-100 dark:hover:bg-blue-900 hover:scale-110 transition-all">
   412→                            <Edit className="w-4 h-4" />
   413→                          </Button>
   414→                          <Button variant="ghost" size="sm" onClick={() => handleDelete(proxy.id)} className="hover:bg-red-100 dark:hover:bg-red-900 hover:scale-110 transition-all">
   415→                            <Trash2 className="w-4 h-4" />
   416→                          </Button>
   417→                        </td>
   418→                      </tr>
   419→                    );
   420→                  })
   421→                )}
   422→              </tbody>
   423→            </table>
   424→          </div>
   425→
   426→          {/* Pagination */}
   427→          {total > LIMIT && (
   428→            <div className="flex justify-between items-center mt-4">
   429→              <Button
   430→                variant="outline"
   431→                onClick={() => setPage(p => Math.max(1, p - 1))}
   432→                disabled={page === 1}
   433→                className="hover:scale-105 transition-transform"
   434→              >
   435→                Previous
   436→              </Button>
   437→              <span className="text-sm text-muted-foreground">
   438→                Page {page} of {Math.ceil(total / LIMIT)}
   439→              </span>
   440→              <Button
   441→                variant="outline"
   442→                onClick={() => setPage(p => p + 1)}
   443→                disabled={proxies.length < LIMIT}
   444→                className="hover:scale-105 transition-transform"
   445→              >
   446→                Next
   447→              </Button>
   448→            </div>
   449→          )}
   450→
   451→          {error && (
   452→            <div className="p-4 bg-destructive/10 border border-destructive/30 rounded-md text-destructive">
   453→              {error}
   454→            </div>
   455→          )}
   456→        </CardContent>
   457→      </Card>
   458→
   459→      {/* Create/Edit Modal */}
   460→      {showModal && (
   461→        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50" onClick={() => setShowModal(false)}>
   462→          <div className="bg-background rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto" onClick={e => e.stopPropagation()}>
   463→            <div className="p-6 border-b">
   464→              <h2 className="text-xl font-semibold">
   465→                {editingId ? 'Edit Proxy' : 'Add New Proxy'}
   466→              </h2>
   467→              <p className="text-sm text-muted-foreground mt-1">
   468→                Configure proxy details. For authentication, enter credentials if required.
   469→              </p>
   470→            </div>
   471→            <form
   472→              onSubmit={async (e) => {
   473→                e.preventDefault();
   474→                const formData = new FormData(e.currentTarget);
   475→                const data: any = {
   476→                  host: formData.get('host') as string,
   477→                  port: parseInt(formData.get('port') as string) || undefined,
   478→                  username: formData.get('username') as string || undefined,
   479→                  password: formData.get('password') as string || undefined,
   480→                  protocol: formData.get('protocol') as string || 'http',
   481→                  pool: (formData.get('pool') as string) || 'default',
   482→                  providerId: formData.get('providerId') as string || undefined,
   483→                  tags: (formData.get('tags') as string)?.split(',').map(t => t.trim()).filter(Boolean) || undefined,
   484→                  meta: formData.get('meta') ? JSON.parse(formData.get('meta') as string) : undefined,
   485→                  disabled: formData.get('disabled') !== 'on', // Active by default unless disabled
   486→                };
   487→                editingId ? handleUpdate(data as UpdateProxy) : handleCreate(data as CreateProxy);
   488→              }}
   489→              className="p-6 space-y-4"
   490→            >
   491→              <div className="grid md:grid-cols-2 gap-4">
   492→                <div>
   493→                  <label htmlFor="host" className="block text-sm font-medium mb-2">
   494→                    Host <span className="text-destructive">*</span>
   495→                  </label>
   496→                  <input
   497→                    id="host"
   498→                    name="host"
   499→                    type="text"
   500→                    defaultValue={editData.host}
   501→                    required
   502→                    placeholder="e.g., proxy.example.com"
   503→                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
   504→                  />
   505→                </div>
   506→                <div>
   507→                  <label htmlFor="port" className="block text-sm font-medium mb-2">
   508→                    Port <span className="text-destructive">*</span>
   509→                  </label>
   510→                  <input
   511→                    id="port"
   512→                    name="port"
   513→                    type="number"
   514→                    defaultValue={editData.port}
   515→                    required
   516→                    min="1"
   517→                    max="65535"
   518→                    placeholder="8080"
   519→                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
   520→                  />
   521→                </div>
   522→              </div>
   523→              <div className="grid md:grid-cols-2 gap-4">
   524→                <div>
   525→                  <label htmlFor="protocol" className="block text-sm font-medium mb-2">
   526→                    Protocol
   527→                  </label>
   528→                  <select
   529→                    id="protocol"
   530→                    name="protocol"
   531→                    defaultValue={editData.protocol}
   532→                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
   533→                  >
   534→                    <option value="http">HTTP</option>
   535→                    <option value="https">HTTPS</option>
   536→                    <option value="socks4">SOCKS4</option>
   537→                    <option value="socks5">SOCKS5</option>
   538→                  </select>
   539→                </div>
   540→                <div>
   541→                  <label htmlFor="pool" className="block text-sm font-medium mb-2">
   542→                    Pool <span className="text-red-500">*</span>
   543→                  </label>
   544→                  <select
   545→                    id="pool"
   546→                    name="pool"
   547→                    defaultValue={editData.pool || ''}
   548→                    required
   549→                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
   550→                  >
   551→                    <option value="">All Pools</option>
   552→                    <option value="residential">Residential</option>
   553→                    <option value="isp">ISP</option>
   554→                    <option value="datacenter">Datacenter</option>
   555→                    <option value="mobile">Mobile</option>
   556→                    <option value="web_unblocker">Web Unblocker</option>
   557→                    <option value="test">Test</option>
   558→                  </select>
   559→                </div>
   560→              </div>
   561→              <div className="grid md:grid-cols-2 gap-4">
   562→                <div>
   563→                  <label htmlFor="providerId" className="block text-sm font-medium mb-2">
   564→                    Provider
   565→                  </label>
   566→                  <select
   567→                    id="providerId"
   568→                    name="providerId"
   569→                    defaultValue={editData.providerId}
   570→                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
   571→                  >
   572→                    <option value="">None</option>
   573→                    {providers.map(p => (
   574→                      <option key={p.id} value={p.id}>{p.name}</option>
   575→                    ))}
   576→                  </select>
   577→                </div>
   578→                <div>
   579→                  <label htmlFor="username" className="block text-sm font-medium mb-2">
   580→                    Username (Optional)
   581→                  </label>
   582→                  <input
   583→                    id="username"
   584→                    name="username"
   585→                    type="text"
   586→                    defaultValue={editData.username || ''}
   587→                    placeholder="username"
   588→                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
   589→                  />
   590→                </div>
   591→              </div>
   592→              <div className="grid md:grid-cols-2 gap-4">
   593→                <div>
   594→                  <label htmlFor="password" className="block text-sm font-medium mb-2">
   595→                    Password (Optional)
   596→                  </label>
   597→                  <input
   598→                    id="password"
   599→                    name="password"
   600→                    type="password"
   601→                    defaultValue={editData.password ? '*****' : ''}
   602→                    placeholder="password (enter new to change)"
   603→                    className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
   604→                  />
   605→                </div>
   606→                <div>
   607→                  <label className="flex items-center gap-2">
   608→                    <input type="checkbox" name="disabled" defaultChecked={editData.disabled} className="rounded" />
   609→                    <span className="text-sm">Disabled</span>
   610→                  </label>
   611→                </div>
   612→              </div>
   613→              <div>
   614→                <label htmlFor="tags" className="block text-sm font-medium mb-2">
   615→                  Tags (comma-separated)
   616→                </label>
   617→                <input
   618→                  id="tags"
   619→                  name="tags"
   620→                  type="text"
   621→                  defaultValue={editData.tags?.join(', ')}
   622→                  placeholder="tag1, tag2, geo-us"
   623→                  className="w-full px-3 py-2 border border-input rounded-md bg-background focus-visible:ring-2 focus-visible:ring-ring"
   624→                />
   625→              </div>
   626→              <div>
   627→                <label htmlFor="meta" className="block text-sm font-medium mb-2">
   628→                  Meta (JSON, optional)
   629→                </label>
   630→                <textarea
   631→                  id="meta"
   632→                  name="meta"
   633→                  rows={3}
   634→                  defaultValue={JSON.stringify(editData.meta, null, 2)}
   635→                  placeholder='{"custom": "value"}'
   636→                  className="w-full px-3 py-2 border border-input rounded-md bg-background font-mono text-sm focus-visible:ring-2 focus-visible:ring-ring"
   637→                />
   638→                <TooltipProvider>
   639→                  <Tooltip>
   640→                    <TooltipTrigger>
   641→                      <span className="text-xs text-muted-foreground mt-1 block cursor-help">(Help)</span>
   642→                    </TooltipTrigger>
   643→                    <TooltipContent>Additional metadata as JSON. Use for custom fields not covered above.</TooltipContent>
   644→                  </Tooltip>
   645→                </TooltipProvider>
   646→              </div>
   647→              <div className="flex gap-3 pt-4 border-t">
   648→                <Button type="button" variant="outline" onClick={() => { setShowModal(false); setEditData({}); setEditingId(null); }}>
   649→                  Cancel
   650→                </Button>
   651→                <Button type="submit" className="ml-auto hover:scale-105 transition-transform">
   652→                  {loading ? 'Saving...' : editingId ? 'Update' : 'Add Proxy'}
   653→                </Button>
   654→              </div>
   655→              {error && <div className="text-destructive text-sm">{error}</div>}
   656→            </form>
   657→          </div>
   658→        </div>
   659→      )}
   660→
   661→      {/* Test Modal */}
   662→      {showTestModal && (
   663→        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50" onClick={() => setShowTestModal(false)}>
   664→          <div className="bg-background rounded-lg shadow-xl max-w-md w-full" onClick={e => e.stopPropagation()}>
   665→            <div className="p-6 border-b">
   666→              <h2 className="text-xl font-semibold">Test Results for Proxy</h2>
   667→            </div>
   668→            <div className="p-6 space-y-4">
   669→              {testResult ? (
   670→                <>
   671→                  <div className={cn('p-3 rounded-md', testResult.success ? 'bg-green-50 border border-green-200' : 'bg-red-50 border border-red-200')}>
   672→                    <p className="font-medium">Status: {testResult.success ? 'Success' : 'Failed'}</p>
   673→                    {testResult.success && testResult.proxyString && (
   674→                      <div className="mt-2 p-2 bg-muted font-mono text-sm rounded">
   675→                        {testResult.proxyString}
   676→                      </div>
   677→                    )}
   678→                    {testResult.error && <p className="text-sm mt-1">Error: {testResult.error}</p>}
   679→                    <p>Score: {testResult.score}%</p>
   680→                  </div>
   681→                  <Button onClick={() => setShowTestModal(false)} className="w-full">
   682→                    Close
   683→                  </Button>
   684→                </>
   685→              ) : (
   686→                <div className="text-center py-8">
   687→                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-2"></div>
   688→                  <p>Testing proxy... (issuing lease and checking connectivity)</p>
   689→                </div>
   690→              )}
   691→            </div>
   692→          </div>
   693→        </div>
   694→      )}
   695→
   696→      <DeleteConfirmModal
   697→        isOpen={showDeleteModal}
   698→        onClose={() => { setShowDeleteModal(false); setPendingDeleteId(null); setRememberChoice(false); }}
   699→        onConfirm={handleConfirmDelete}
   700→        itemType="proxy"
   701→        rememberChoice={rememberChoice}
   702→        onRememberChange={handleRememberChange}
   703→      />
   704→    </div>
   705→  );
   706→}

