    10→import { getProxies, createProxy, updateProxy, deleteProxy, issueLease, getProviders, getProxy, type Proxy, type CreateProxy, type UpdateProxy, type Provider } from '../lib/api';
    11→import { DeleteConfirmModal } from '../components/DeleteConfirmModal';
    12→
    13→const LIMIT = 10;
    14→
    15→interface LeaseResponse {
    16→  leaseId: string;
    17→  proxy: string;
    18→  protocol: string;
    19→  expiresAt: string;
    20→  meta: {
    21→    providerId?: string;
    22→    score: number;
