    31→  error?: string;
    32→  score: number;
    33→}
    34→
    35→export default function Proxies() {
    36→  const [proxies, setProxies] = useState<Proxy[]>([]);
    37→  const [loading, setLoading] = useState(true);
    38→  const [error, setError] = useState<string | null>(null);
    39→  const [success, setSuccess] = useState<string | null>(null);
    40→  const [search, setSearch] = useState('');
    41→  const [selectedPool, setSelectedPool] = useState('');
    42→  const [selectedProvider, setSelectedProvider] = useState('');
    43→  const [page, setPage] = useState(1);
    44→  const [total, setTotal] = useState(0);
    45→  const [showModal, setShowModal] = useState(false);
    46→  const [editingId, setEditingId] = useState<string | null>(null);
    47→  const [editData, setEditData] = useState<any>({});
    48→  const [showTestModal, setShowTestModal] = useState(false);
    49→  const [testingId, setTestingId] = useState<string | null>(null);
    50→  const [testResult, setTestResult] = useState<TestResult | null>(null);
    51→  const [togglingId, setTogglingId] = useState<string | null>(null);
    52→  const [providers, setProviders] = useState<Provider[]>([]);
    53→  const [fetchingProviders, setFetchingProviders] = useState(false);
    54→  const [showDeleteModal, setShowDeleteModal] = useState(false);
    55→  const [pendingDeleteId, setPendingDeleteId] = useState<string | null>(null);
    56→  const [rememberChoice, setRememberChoice] = useState(false);
    57→
    58→  // Fetch providers for filter dropdown
    59→  useEffect(() => {
    60→    const fetchProviders = async () => {
    61→      setFetchingProviders(true);
    62→      try {
    63→        const data = await getProviders({ limit: 100 }); // Fetch all for dropdown
    64→        setProviders(data.items);
    65→      } catch (err) {
    66→        console.error('Failed to fetch providers:', err);
    67→      } finally {
    68→        setFetchingProviders(false);
    69→      }
    70→    };
    71→    fetchProviders();
    72→  }, []);
    73→
    74→  const fetchProxies = useCallback(async () => {
    75→    try {
    76→      setLoading(true);
    77→      setError(null);
    78→      const params: any = {
    79→        page,
    80→        limit: LIMIT,
    81→      };
    82→      if (search) params.search = search; // Assuming API supports search; if not, client-side
    83→      if (selectedPool) params.pool = selectedPool;
    84→      if (selectedProvider) params.providerId = selectedProvider;
    85→      const data = await getProxies(params);
    86→      setProxies(data.items);
    87→      setTotal(data.total);
    88→    } catch (err) {
    89→      setError(err instanceof Error ? err.message : 'Failed to fetch proxies');
    90→    } finally {
    91→      setLoading(false);
    92→    }
    93→  }, [page, search, selectedPool, selectedProvider]);
    94→
    95→  useEffect(() => {
    96→    fetchProxies();
    97→  }, [fetchProxies]);
    98→
