   108→      await createProxy(data);
   109→      fetchProxies();
   110→      setShowModal(false);
   111→      setEditData({});
   112→    } catch (err) {
   113→      setError(err instanceof Error ? err.message : 'Failed to create proxy');
   114→    }
   115→  };
   116→
   117→  const handleUpdate = async (data: UpdateProxy) => {
   118→    if (!editingId) return;
   119→    try {
   120→      await updateProxy(editingId, data);
   121→      fetchProxies();
   122→      setEditingId(null);
   123→      setShowModal(false);
   124→      setEditData({});
   125→    } catch (err) {
   126→      setError(err instanceof Error ? err.message : 'Failed to update proxy');
   127→    }
   128→  };
   129→
   130→  const handleToggle = useCallback(async (id: string, currentDisabled: boolean) => {
   131→    if (togglingId === id) return;
   132→    setTogglingId(id);
   133→    try {
   134→      // Optimistic update
   135→      setProxies(prev => prev.map(p => p.id === id ? { ...p, disabled: !currentDisabled } : p));
   136→      await updateProxy(id, { disabled: !currentDisabled });
   137→      fetchProxies(); // Sync
   138→    } catch (err) {
   139→      // Revert
   140→      setProxies(prev => prev.map(p => p.id === id ? { ...p, disabled: currentDisabled } : p));
   141→      setError(err instanceof Error ? err.message : 'Failed to toggle proxy status');
   142→    } finally {
   143→      setTogglingId(null);
   144→    }
   145→  }, [togglingId]);
   146→
   147→
