import { LayoutDashboard, Settings, Users } from 'lucide-react'
import { Link } from 'react-router-dom'

const navItems = [
  { name: 'Dashboard', path: '/', icon: LayoutDashboard },
  { name: 'Proxies', path: '/proxies', icon: Users },
  { name: 'Providers', path: '/providers', icon: Users },
  { name: 'Settings', path: '/settings', icon: Settings },
]

export default function Sidebar() {
  return (
    <aside className="w-64 h-screen bg-gray-900 text-white p-4 flex flex-col space-y-4">
      <h1 className="text-xl font-bold">ProxyHub Admin</h1>
      <nav className="flex-1">
        {navItems.map((item) => {
          const Icon = item.icon
          return (
            <Link
              key={item.name}
              to={item.path}
              className="flex items-center space-x-2 p-2 rounded hover:bg-gray-800"
            >
              <Icon className="w-5 h-5" />
              <span>{item.name}</span>
            </Link>
          )
        })}
      </nav>
    </aside>
  )
}

import { Moon, Sun } from 'lucide-react'
import { useEffect, useState } from 'react'

export default function ThemeToggle() {
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem('theme') === 'dark')

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark')
      localStorage.setItem('theme', 'dark')
    } else {
      document.documentElement.classList.remove('dark')
      localStorage.setItem('theme', 'light')
    }
  }, [darkMode])

  return (
    <button
      onClick={() => setDarkMode(!darkMode)}
      className="p-2 rounded hover:bg-gray-200 dark:hover:bg-gray-700"
      aria-label="Toggle theme"
    >
      {darkMode ? <Sun className="w-5 h-5" /> : <Moon className="w-5 h-5" />}
    </button>
  )
}

import { useEffect, useState } from 'react'
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card' // Assume shadcn/ui or similar

const API_BASE = import.meta.env.VITE_API_URL || 'http://localhost:8080'

interface Proxy {
  id: string
  host: string
  score: number
  // Add more fields as needed
}

export default function Dashboard() {
  const [proxies, setProxies] = useState<Proxy[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  useEffect(() => {
    fetch(`${API_BASE}/v1/proxies/sample`)
      .then((res) => {
        if (!res.ok) throw new Error('Failed to fetch proxies')
        return res.json()
      })
      .then((data) => {
        setProxies(data.items || [])
        setLoading(false)
      })
      .catch((err) => {
        setError(err.message)
        setLoading(false)
      })
  }, [])

  if (loading) return <div className="p-8">Loading...</div>
  if (error) return <div className="p-8 text-red-500">{error}</div>

  const avgScore = proxies.reduce((sum, p) => sum + p.score, 0) / proxies.length || 0
  const totalProxies = proxies.length

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
      <Card>
        <CardHeader>
          <CardTitle>Total Proxies</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-3xl font-bold">{totalProxies}</p>
        </CardContent>
      </Card>
      <Card>
        <CardHeader>
          <CardTitle>Average Score</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-3xl font-bold">{avgScore.toFixed(2)}</p>
        </CardContent>
      </Card>
      {/* Add more cards for stats */}
      <Card>
        <CardHeader>
          <CardTitle>Proxy List</CardTitle>
        </CardHeader>
        <CardContent>
          <ul className="space-y-2">
            {proxies.slice(0, 5).map((proxy) => (
              <li key={proxy.id} className="text-sm">{proxy.host} - Score: {proxy.score}</li>
            ))}
          </ul>
        </CardContent>
      </Card>
    </div>
  )
}

server {
    listen 80;
    server_name localhost;
    location / {
        root /usr/share/nginx/html;
        index index.html index.htm;
        try_files $uri $uri/ /index.html;
    }
    error_page   500 502 503 504  /50x.html;
    location = /50x.html {
        root   /usr/share/nginx/html;
    }
}

