import { useState } from 'react';
import { BarChart, XAxis, YAxis, Bar, PieChart, Pie, Tooltip, ResponsiveContainer, Cell } from 'recharts';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

interface UsageData {
  date: string;
  responses: number;
}

interface ResponseCodeData {
  name: string;
  value: number;
}

const UsageChart = () => {
  const [data, setData] = useState<any>(null); // Replace with data fetching from /v1/usage/stats

  // TODO: Fetch from API
  // useEffect(() => {
  //   fetch(`${API_BASE}/v1/usage/stats`).then(res => res.json()).then(setData);
  // }, []);

  // Hardcoded placeholder
  const dailyResponses = [
    { date: "2025-10-24", responses: 123 },
    { date: "2025-10-25", responses: 456 },
  ];

  const responseCodes = [
    { name: '200', value: 800 },
    { name: '3xx', value: 100 },
    { name: '4xx', value: 50 },
    { name: '5xx', value: 20 }
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 bg-white dark:bg-gray-800 p-4 rounded-lg shadow-md dark:shadow-blue-900">
      <div>
        <h3 className="text-xl font-semibold text-gray-900 dark:text-gray-100">Responses / Day (Last 7 Days)</h3>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={dailyResponses}>
            <XAxis dataKey="date" stroke="hsl(var(--muted-foreground))" />
            <YAxis stroke="hsl(var(--muted-foreground))" />
            <Tooltip contentStyle={{ background: "hsl(var(--card))", color: "hsl(var(--card-foreground))" }} />
            <Bar dataKey="responses" fill="hsl(var(--primary))" />
          </BarChart>
        </ResponsiveContainer>
      </div>
      <div>
        <h3 className="text-xl font-semibold text-gray-900 dark:text-gray-100">Response Codes</h3>
        <ResponsiveContainer width="100%" height={300}>
          <PieChart>
            <Pie data={responseCodes} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={80}>
              {responseCodes.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip contentStyle={{ background: "hsl(var(--card))", color: "hsl(var(--card-foreground))" }} />
          </PieChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default UsageChart;

import { useState, useEffect } from 'react';

const ActivityLog = () => {
  const [logs, setLogs] = useState<string[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch last 50 lines from /logs/notifications.json
    fetch('/logs/notifications.json') // Assume served or via API
      .then(res => res.json())
      .then(data => {
        const last50 = data.slice(-50); // Assume array of log objects, adjust as needed
        setLogs(last50.map(log => JSON.stringify(log)));
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  if (loading) return <div>Loading logs ...</div>;

  return (
    <div className="bg-white dark:bg-gray-800 p-4 rounded-lg shadow-md dark:shadow-blue-900">
      <h3 className="text-xl font-semibold text-gray-900 dark:text-gray-100">Activity Log (Last 50)</h3>
      <div className="max-h-96 overflow-y-auto mt-2 text-gray-700 dark:text-gray-300 font-mono text-sm">
        {logs.map((log, index) => (
          <p key={index} className="border-b border-gray-200 dark:border-gray-700 py-1">{log}</p>
        ))}
      </div>
    </div>
  );
};

export default ActivityLog;

